css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document url-prefix("http://ficbook.net/readfic/") {
BODY{font-family:Tahoma!important;background:url("http://i1144.photobucket.com/albums/o492/SamaelDark/web/bk_wrapper.png") repeat 0 0 fixed #1E201F!important}
DIV.sprites_logo_smaller{background-image:none!important}
small{font-size:100%}
A{text-decoration:none!important;color:#000!important}
A.white{color:#fff!important}
.menu A:hover{text-shadow:0px 0px 6px rgba(255,255,255,0.7)!important}
A.white:hover{color:#fff!important}
A.user_home{border-bottom:none!important}
A.user_home:hover{text-shadow:none!important}
.public_beta{white-space:normal}
TABLE{font-size:12px}
TABLE.loggedin_bar{height:53px!important;width:800px!important}
TD.loggedin_cell{height:53px}
TD.home_menu{width:160px!important}
DIV.sprites_menu_left,td[background="//teinon-st.cdn.ngenix.net/design/menu/menu_02.png"],td[background="//teinon-st.cdn.ngenix.net/design/book/top/book_01.png"],td[background="//teinon-st.cdn.ngenix.net/design/book/left/book_02.png"],td[background="//teinon-st.cdn.ngenix.net/design/book/right/book_02.png"],td[background="//teinon-st.cdn.ngenix.net/design/book/bottom/book_02.png"],DIV.sprites_menu_right,
DIV.sprites_book_left_top,DIV.sprites_book_right_top2,DIV.sprites_book_right_top3,DIV.sprites_book_right_top,DIV.sprites_book_left,DIV.sprites_book_left2,DIV.sprites_book_left_bottom,DIV.sprites_book_left_bottom2,DIV.sprites_book_right_bottom,DIV.sprites_book_right_bottom2,TD.main_cell{background-image:none!important;background-color:transparent!important}
td[width="516"]{width:0}
DIV.author_list{width:auto!important}
DIV.author_list DIV{width:50%!important}
table[align="right"]{background:url("http://i1144.photobucket.com/albums/o492/SamaelDark/web/bk_wrapper.png") repeat 0 0 #CCC!important;width:800px!important;position:absolute;margin-left:-400px;left:50%}
.menu{background-color:rgba(181, 243, 255, 0.5)}
TD.main_cell{background-color:transparent!important}
TD.dop_nav,TD.top_banner_cell{position:absolute;right:20px;top:30px;text-align:right}
DIV.part_text{width:95% !important;font-size:14px!important}
DIV.submit_button INPUT{width:100px;padding:6px;background-color:#FFF;border:none;box-shadow:4px 4px 16px #444;color:#000}
TABLE.comment_form_table{margin-top:40px!important}
A.like {border:none !important;background:none !important;color:#C50000!important}
TEXTAREA,DIV.comment_thumb{box-shadow:4px 4px 12px #444;background-color:rgba(255, 255, 255, 0.75);border:none!important}
DIV.user_drop_down[style="position: absolute; left: -65px; z-index: 100; display: block;"]{left:400px!important;top:84px!important;color:#fff!important;border:none!important;font-size:100%!important;float:none!important;width:200px!important;text-align:center!important;background-color:#1f1f1f!important;display:none;box-shadow:4px 4px 16px #444}
DIV.user_drop_down_separator{border:1px solid #fff!important}
a.give_present,a.give_reward_fic,a.give_present_small,TD.zakl,TD.footer,DIV.choose_font,DIV.no_flood_warning,table[height="48"],DIV.sprites_book_left_bottom,DIV.sprites_book_right_bottom2,HR,DIV.sprites_book_left,DIV.sprites_book_right_top2,DIV.sprites_book_right_top3
,.main_cell .part_text .public_beta_info
/*,noscript*/
,.ad_container
,.fanfic_edit_menu + table + div[align="center"]
,#comments .content .comment_thumb .comment_text .comment_avatar
{
    display:none!important
}
html body table.top_bar + table
{
    max-width: 800px!important;
    margin: 0px auto!important;
}
#comment_container form[action*="#commentfrom"]
{
    /*margin-top:100px!important;*/
}
DIV.criticism_message
{
    margin-bottom: 100px!important;
}
DIV.user_drop_down
{
  float: left !important;
  left: 248px !important;
  text-align: left !important;
  top: 70px !important;
}
.main_cell>h1
{
    margin-top:0px!important;
}
}
