css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sites.google.com") {
    #sites-chrome-everything a {text-decoration: underline;}
    body {background-color: white;}
      /* needed 
    #sites-canvas-wrapper { display: none; }
    #sites-header-title { display: none; }
    */
    #sites-chrome-main-wrapper {border: 0px;}
    #sites-chrome-footer-wrapper  {border: 0px;}
    
    /* not needed */
    #sites-chrome-header-wrapper {display: none; height: 0px; background-color: white; color:white;}
    #sites-chrome-sidebar-left, #sites-chrome-sidebar-right, .sites-canvas-sidebar  { display: none; }
    .sites-attachements-row {display: none; height: 0px; background-color: white; color:white;}
    .sites-attachments-separator {display: none; height: 0px; background-color: white; color:white;}
    .COMP-page-comments {display: none; height: 0px; background-color: white; color:white;}
    .sites-symbol {display: none; height: 0px; background-color: white; color:white;}
    .sites-attachments-button-element {display: none; height: 0px; background-color: white; color:white;}
    .sites-comment-docos-wrapper {display: none; height: 0px; background-color: white; color:white;}
    .sites-adminfooter  {display: none; height: 0px; background-color: white; color:white;}
}
