css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
#dashboard_index {background: #365470; }
#dashboard_messages_inbox {background: #365470; }
#settings_actions_tumblelog {background: #365470; }
#dashboard_useraction_following {background: #365470; }

div.l-header .logo { float: left!important; position:fixed; align: left; top: -2px; }
div.l-header { height:0px; }
div.l-header #tabs_outer_container { float: left!important; position: relative; left: 325px; }
div.l-header .tab_bar { float: left!important; }
.tab_bar .tab { float: left; }
div.l-header .tab.iconic.ui_search { padding: 0 16px; left: 55px; }

div.l-header .search_form_row { background-color: transparent; }


#container .clearfix {border-radius: 0px; }

#right_column { float: left!important; position:fixed; background: transparent; top: 75px; overflow-y: scroll; overflow-x:hidden; height: 80%; }

#right_column:after {content: none; }

#left_column { float: right!important; }

#auto_pagination_loader { background: #2c4762 }


::-webkit-scrollbar-track-piece{
	background-color:transparent;/*滚动条的背景颜色*/
	-webkit-border-radius:0;/*滚动条的圆角宽度*/
}
::-webkit-scrollbar{
	width:8px;/*滚动条的宽度*/
	height:8px;/*滚动条的高度*/
}
::-webkit-scrollbar-thumb:vertical{/*垂直滚动条的样式*/
	height:50px;
	background-color:transparent;
	-webkit-border-radius:4px;

}
::-webkit-scrollbar-thumb:hover{/*滚动条的hover样式*/
	height:50px;
	background-color:#9f9f9f;
	-webkit-border-radius:4px;
}
::-webkit-scrollbar-thumb:horizontal{/*水平滚动条的样式*/
    width: 5px;
    background-color: #CCCCCC;
    -webkit-border-radius: 6px;
}
}
