css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fm.baidu.com") {
  .fm-header .fm-logo { display: none }
  .page-fm-channel .page-tip { display: none }
  #fm-panel .fm-footer { display: none }
  
  @media screen and (max-width: 900px) {
    .fm-channel { left: 20px }
    .fm-player { left: 20px }
    .fm-main .fm-lrc { left: 280px }

    #fm-panel .fm-main, #fm-panel .fm-aside, #fm-panel .fm-content { top: 70px; }

    .page-fm-channel .scene-panel li { margin-right: 5px; }
    .page-fm-channel .scene-panel li, .page-fm-channel .channel-panel li { width: 100px;}
    .page-fm-channel .channel { width: 100px; margin: 0px 5px -15px 0px }
    .page-fm-channel .channel.special .channel-item.private i,
    .page-fm-channel .channel.special .channel-item.heart i,
    .page-fm-channel .channel.special .channel-item.musician i { margin-left: -20px; }

    #fm-panel .fm-page { width: 640px; }
    #fm-panel .next-page, #fm-panel .prev-page { width: 70px; }
    #fm-panel .next-page .arrow { margin: 0px -30px 0px 0 }
    #fm-panel .prev-page .arrow { margin: 0px 0px 0px -30px }

    .page-fm-channel .scene-panel, .page-fm-channel .channel-panel { margin-right: 0 }

    .page-fm-channel .scene-panel { margin-bottom: 10px }
    .page-fm-channel .channel li { margin-bottom: 4px; height: 24px; line-height: 24px }
    #fm-panel .fm-page { margin-bottom: 0 }
  }
}
