@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* UX-FXChrome Style Recalibrated */

/* 1.  Background Tab Separators */

#tabbrowser-tabs[movingtab] > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([first-visible-tab])::before, #tabbrowser-tabs:not([overflow]) > .tabbrowser-tab[last-visible-tab]:not([selected])::after  {
  -moz-margin-start: -1px!important;
  background-image: linear-gradient(rgba(10,31,51,0), rgba(10,31,51,0.3))!important;
  background-position: left bottom!important;
  background-repeat: no-repeat!important;
  background-size: 1px 100%!important;
  content: ''!important;
  display: inline-block!important;
  height: 27px!important;
  width: 1px!important;
  margin-top: -2px !important;
  margin-bottom: 2px !important;
}

.tabbrowser-tab:hover {
  background-image: -moz-radial-gradient(center bottom, farthest-corner, rgba(255,255,255,0.80) 3%, rgba(250,185,185,0.75) 20%, rgba(255,128,128,0.25) 40%, rgba(255,128,128,0) 70%)!important;
  background-position: center bottom 2px!important;
  background-repeat: no-repeat!important;
  background-size: 85% 100%!important;
}

#TabsToolbar .tabbrowser-tab[pinned]:not([selected="true"]) .tab-background {
    display: none !important;
}

#TabsToolbar .tabbrowser-tab:not([selected="true"]) .tab-background {
    display: none !important;
}

.tabs-newtab-button {
list-style-image: none !important;
border: none !important;
background: no-repeat center url(data:image/png;base64,) !important;}

.tabs-newtab-button:hover {
background: no-repeat center url(data:image/png;base64,) !important;}
