@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("respublika.lt") {
    /* compact header */
    #header_block {
        background-color: white !important;
    }
    #header_block .logo_block img {
         margin-top: -25px !important;
    }
    #header_block .logo_block p.data {
        display: none !important;
    }
    #header_block .menu_block {
         margin-top: 15px !important;
    }
    #header_block .menu_block #top_menu_1 {
        margin-top: 5px !important;
    }
    #top_menu_2 {
        margin-top: -20px !important;
    }
    /* no ads */
    .banner_top_block,
    #content_left_block_135,
    #content_left_block_167 .block_135 {
        display: none !important;
    }
    #content_mid_block,
    .block_568 {
        width: 710px !important;
    }
    .block_568_content,
    .block_568_bottom_bg,
    .block_568_date,
    .block_568_text,
    .block_568_video,
    .block_568_text_links, 
    .com_info,
    .com_list2,
    .com_list2 li, 
    .com_list2 li.first,
    .news_gallery, 
    .news_gallery table[width='540'], 
    .news_gallery_imgb, 
    .news_gallery_table {
        width: 680px !important;
    }
    .block_568_bottom_bg {
        background: none !important;
    }
    /* Video sizes */
    #Flash0 {
        left: 0 !important;
    }
    .block_568_video td.ptop_27 > table {
        width: 640px !important;
    }
    #Flash0_Inside {
        width: 640px !important;
        height: 532px !important;
    }
