css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("unity3d.com"),
regexp('file:///.*Unity.*/Documentation/.*'){
    
    html, body{
       /*background-image : none !important;*/
       background-color : #333 !important;
       color : #AAA !important;
    }
    input.req[type="text"], input.req[type="tel"], input.req[type="email"], input.req[type="password"], textarea.req{
        background-color : #333;
        color : #AAA;
        border-color : #555;
    }
    
    .failed{
        background-color : #702222;
    }
    
    /* Login */
    section.content{
        background-color: #444;
    }
    .content header ul li{
        border-color : #555;
    }
    .content header ul li a{
        background-color: #333;
    }
    .bg-bl{
        background-color : #333;
    }
    /* */
    #content {
        background-color : #333 !important;
    }
    p, em, h1, h2, h3, h4, h5, .heading, li {
        color : #A8A8A8 !important;
    }
    a {
    	color : #68B !important;
    }
    th {
        background-color : #444 !important;
    }
    table, td, tbody {
        border-color : #555 !important;
    }
    .blue-btn, a.active-trail, .selected{
        color : #9CF !important;
    }
    
    /* License Comparison */
    
    tr.vis[style]{
        background-color : #444 !important;
    }
    
    /* Forums */
    .ugc a:hover, .ugc a:focus{
        box-shadow : 0px 0px 0px 0px #333;/* Replace broken box shadow */
    }
    
    div.pageContent, .forumNodeInfo{
        background-color : #404040 !important;   
    }
    .messageList .message, .nav-container{
       border-color : #555;
    }
    .messageList .message:nth-of-type(2n){
        background-color : #383838;
    }
    .message:nth-of-type(2n){
        background-color : #383838;
    }
    
    body footer{
        background-color : #333;
    }
    
    .heading, .textHeading{
        border-color : #666;
    }
    .secondaryContent, .subHeading{
        background-color : #555;
        border-color : #666;
    }
    
    .attachedFiles, .attachedFiles .attachedFilesHeader{
        background-color : #333;
        border-color : #555;
    }
    .attachedFiles .attachmentList{
        background-image : none;
        background-color : #333;
        border-color : #555;
    }
    .attachment .boxModelFixer{
        background-color : #222;
        border-color : #555;
    }
    
    /* - Editing */
    .redactor_box{
        border-color : #555;
    }
    .redactor_toolbar{
        background-color : #666;
        border-color : #555;
    }
    
    #exposeMask{
        background-color : #222 !important;
    }
    
    /* - Forum Navigation */
    .breadcrumb{
         border-color : #555;
    }
    .breadcrumb,  .breadcrumb .crust a.crumb{
        background-color : #333;
    }
    .breadcrumb .crust:hover a.crumb{
        background-color : #555;
    }
    .breadcrumb .crust .arrow span{ /* Inner arrow*/
       border-color : rgba(0, 0, 0, 0) #000000 rgba(0, 0, 0, 0) #333;
    }
   .breadcrumb .crust:hover .arrow span {
       border-left-color: #555;
    }
    
    .PageNav a{
        background-color : #333 !important;
        border-color : #555 !important;
    }
    .button {
        background-color : #666;
        border-color : #333;
    }
    .button:hover{
        background-color : #888;
    }
    .button.primary{
        background-color : #555;
    }
    .button.primary:hover{
        background-color : #666;
    }
    
    .nodeList .categoryStrip{
        background-color : #333;
        border-color : #666;
    }
    .nodeList .forumNodeInfo{
        border-color : #666;
    }
    
    .itemPageNav a{
        background-color : #666;
        border-color : #777;
    }
    .itemPageNav a:hover{
        background-color : #555;
        border-color : #777;
    }
    .navTabs .navTab.selected .tabLinks a:hover {
        background-color : #555;
    }
    
    .Menu .sectionFooter{
        background-color : #444;
    }
    
    /* - Thread list */
    .discussionListItem .posterAvatar{
        background-color : #666 !important;
    }
    .discussionList .sectionHeaders{
        background-color : #353535 !important;
        border-color : #555 !important;
    }
    .discussionListItem{
        background-color : #707070 !important;
        border-color : #444 !important;
    }
    .discussionListItem:nth-of-type(2n){
        background-color : #686868 !important;
    }
    .discussionListItem.unread{
        background-color : #555 !important;
        border-color : #444 !important;
    }
    .discussionListItem.unread:nth-of-type(2n){
        background-color : #525252 !important;
    }
    .discussionListItem.sticky{
        background-color : #444 !important;
        border-color : #555 !important;
    }
    .discussionListItem .stats{
        background-color : #555 !important;
    }
    .discussionListItem .stats dl{
        border-color : #666 !important;
    }
    
    /* - User Menu */
    .Menu, .Menu .primaryContent, .Menu .secondaryContent, .Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen{
        background-color : #333;
        border-color : #222;
    }
    .blockLinksList a:hover{
        background-color : #444;
    }
    
    /* - Quotes */
    .bbCodeQuote{
        border-color : #222;
    }
    .bbCodeQuote .attribution{
        background-color : #222;
    }
    .bbCodeQuote .quoteContainer{
        background-color : #383838;
    }
    .bbCodeQuote .quoteContainer .quoteExpand{
        background : linear-gradient(to bottom, rgba(51, 51, 51, 0) 0%, #222 80%) repeat scroll 0 0 rgba(0, 0, 0, 0);
        
    }
    
    /* - Code */
    .bbCodeBlock, .bbCodeBlock .type{
        border-color : #555;
    }
    .bbCodeBlock .type, .bbCodeBlock pre, .bbCodeBlock .code{
        background-color : #333;
    }
    
    /***
     * - Syntax Highlighting
     ***/
    /* -- Plain*/
    div[style="font: normal normal 1em/1.2em monospace; margin:0; padding:0; background:none; vertical-align:top;"]{
       color : #AAA !important;
    }
    /* -- strings */
    span[style="color: #a31515;"]{
        color : #FF3B20 !important;
    }
    /* -- Keywords*/
    span[style="color: #008de4;"]{
       color : #46E !important;
    }
    /* -- Comments*/
    span[style="color: #008000; font-style: italic;"]{
       color : #666 !important;
    }
    /* -- Type*/
    span[style="color: #2b91af;"]{
       color : #DA2 !important;
    }
    /* -- literals */
    span[style="color: #0000c0;"]{
        color : #2C2 !important;
    }
    /* -- typeof, new, #pragma*/
    span[style="color: #008000;"]{
        color : #AAE !important;
    }
    
    /* -- known methods */
    span[style="color: #8b6ba6;"]{
        color : #8AF !important;
    }
    span[style="color: #813400;"]{
        color : #88C !important;
    }
    
    
    
    /* Top Bar */
                   
    .top-nav ul li a{
        /* Fixing the font not being the same on every page: */
        font-family : "KlavikaMedium",Helvetica,Arial,sans-serif;
        text-align : center;
        /*font-weight : 600;*/
        /*font-size : 15px;*//* Slightly narrower font than standard to make sure the store button always fits */
    }
    .top-nav .active-trail, .top-nav a.selected{/*2573A9  1a5278*/
        background : linear-gradient(to bottom, #2573A9 0%, #081925 100%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    }
                   
    .sub-nav{
        background-color : #081925 !important;
    }
    .sub-nav ul{
        background : linear-gradient(to bottom, #081925 10%, #2573A9 100%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    }


    /* Documentation Specific */
    
    .toolbar, .nextprev, div.footer-wrapper{
        background-color : #444 !important;
    }
    
    .sidebar-wrap{
        background-color : #444 !important;
        padding : 4px;
    }
    
    input[type="text"], select, textarea{/* Search Bar */
        background-color : #888 !important;
    }

                   
    div.sidebar-menu, div.sidebar-menu h2{
        border-color : #555 !important;
    }
    
    .learn-docs div.breadcrumbs{
        background : linear-gradient(to right, #292929 0%, #383838 20%, #383838 80%, #292929 100%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    }
     ul.left-menu{
    	background-color : #383838 !important;
    }
                   
    .codelisting, .codeExampleJS, .codeExampleCS, .codeExampleBoo, .codeExampleRaw, pre, pre code{
        color : #B5B5B5 !important;
        background-color : #383838 !important;
        border-color : #666666 !important;
    }
    .script-lang ul li{
        background-color : #444 !important;
    }
    div.toolbar div.script-lang ul li:hover, div.toolbar div.script-lang ul li.selected{
        background-color : #222 !important;
        color : #CCC !important;
    }
    div.toolbar div.script-lang div.dialog{
        background-color : #444 !important;
    }
    a.cd {
        color : #A8A8A8 !important;
    }
    
    /* Wiki */
    #mw-page-base, .mediawiki{
        background : none #222 !important;
    }
    .toc{
        background-color : #444 !important;
    }
                   
    /* Release Notes */
    .bl{
        background-color : #383838 !important;
    }
    div.whatsnew-menu ul li{
        border-color : #A8A8A8 !important;
    }
    .node-type-unity-release div.notes h3{
        background-color : #888888 !important;
        color : #383838 !important;
    }
    
    /* Publisher Administration */
    .tableView td{
        color : #888;
    }
                   
}
