css:
/* Simplechan Dark */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("4chan.org")

{body[bgcolor] {
background-color: #000000 !important;
background-image: none !important; 
color: #494949 !important; /* #00bb00 (Green)*/
font-weight: bold;}
a {font-weight:bold;}

div#logo {background: url(http://i.4cdn.org/g/1390842451744.png)/*(http://img63.imageshack.us/img63/4570/logova.png)*/ !important}

* {border: none !important;}
.yotsuba_b_new #quote-preview.highlight, div.opContainer, .preview, .reply:target, .reply.highlight, .reply, .reply:hover, * {border-color: #494949 !important;}
.reply:target, .reply.highlight {border-width: 2px !important; border-style: solid !important;}

body, #content, #settingsMenu, td.postblock, tr, div.extPanel, strong, span.button, div#theme.panel {color: #494949!important; background-color: #000000 !important; background-image: none !important;}

div.post, div.post-body, div.post.reply, .dd-menu li { background-color: #131313 !important; }

div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover, .boardnav .pageJump a, .mFileInfo, div.postLink span.info, a:hover, a.replylink:not(:hover),  div.post div.postInfo span.postNum a:hover, .tu-error, s:hover a, #qrError a, #qrError a:hover,  .yotsuba_b_new .backlink a:hover {color: #494949 !important; }

s:hover {color: #494949!important; background-color: #000000 /*#333*/ !important; background-image: none !important;}
s { color: #333333 !important; background: #000000 !important; text-decoration: none;}

div#boardNavDesktop {width: 99%;text-align: center;padding: .4em;}

div.boardSelect > strong, .stylechanger, div#boardNavDesktopFoot, div#absbot, div.postingMode, div.boardBanner, div.stylechanger, tr.rules, li.fill {display:none !important;}

div#boardNavMobile select, pre.prettyprint {border-style:solid !important; border-width:2px !important; border-color: #494949 !important; background-color: #888 !important;}

span.pln {color: #fff !important}

div.post div.postInfoM span.postNum a, div.postInfo span.postNum a , div.post div.postInfoM span.dateTime, .burichan_new .backlink a, .yotsuba_b_new .backlink a, span.summary, span.name, span.subject, span.postertrip, a, div, td, dt, span.abbr, a.quotelink, span.deadlink, .posteruid, .hand:hover, div.post div.postInfo span.nameBlock span.name, input, div.post div.postInfo span.nameBlock span.postertrip {color: #494949 !important;}


.recaptchatable #recaptcha_response_field:focus, input[type="text"]:focus, textarea:focus { border-color: #494949 !important }

td, div, li, H3, html, hr, span.abbr  {background:transparent !important;}

 .recaptchatable #recaptcha_response_field {border-color: #000000 !important}
 div.postLink a, div.postLink a:hover, .button, div#boardNavMobile select, .recaptchatable, #recaptcha_area tr, #recaptcha_area td, #recaptcha_area th, input, textarea, input[type="submit"], input[type="button"], input[type="file"], input[type="checkbox"], input[type="password"]:focus, #postFile {color: #494949 !important; background-color: #000000 !important; border: 1px solid #131313 !important;;}

input[type="submit"], input[type="button"], input[type="file"], td {font-size: 8pt}

.newPostsMarker:not(#quote-preview) {box-shadow: 0px 3px #494949;}

select#size-ctrl, select#order-ctrl, select#teaser-ctrl {color: #494949; background-color: #131313;}

@-moz-document domain("boards.4chan.org") {  /* Rounded corners starts here */

  .reply, .replyhl {
      border-radius: 5px;
      width: auto;
  }

}  
 
}
/* Rounded corners ends here */
