css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org") {
div#content h1, div#content h2, div#content #firstHeading, h4{
    font-family: "Ubuntu",Georgia,Times,serif;
    line-height: 1.3;
    margin-bottom: 0.25em;
    padding: 0px;
}
    div#content h1, div#content #firstHeading {
    text-align: center;
}
html, body {
    font-family: Open Sans;
}
      #left-navigation,
  #right-navigation li,
  #siteNotice,
  #footer,
  a[title="Learn about new features"],
  .vectorTabs,
  .ambox,
  .plainlinks>tbody>tr>td>a>img, 
  .portal,
   .magnify,
    #p-logo a,
   #siteSub,#p-logo,
#toctitle{
  display:none !important
}
      a{
    color:#777 !important;
    background-color:transparent !important;
    padding-left:2px;
    padding-right:2px;
    -moz-transition:all 0.3s !important;
    -webkit-transition:all 0.3s !important;
    -o-transition:all 0.3s !important;
    transition:all 0.3s !important;
    text-decoration:none !important;
    border-radius:3px;
  }
  a:hover{
    color:white !important;
      background-color:#777 !important; 
  }
  .firstHeading{
    font-size:40px !important;
    background-position:center!important;
  }
div#content {
    margin-left: 3em!important;
    margin-top: -45px;
    margin-right: 3em!important;
    border-width: 0px 0px 0px 0px;}
div.thumbinner,#toc, .toc, .mw-warning,.catlinks,.navbox,.infobox
    {
    border: 0px solid #DADADA;
    padding: 6px !important;
    border-radius: 1.6%;
    }
html .thumbimage {
    border: 0px solid #CCC;
}
    #right-navigation {
    float: left;
    margin-top: 0;
    margin-left:3em
}
    div#simpleSearch {
    display: block;
    width: 10em;
    height: 1.4em;
    margin-top: 0.65em;
    position: relative;
    min-height: 1px;
    border: 1px solid #E0E0E0;
    color: transparent;
    background-color: transparent;
    background-image:none!important;
    background-position: left top;
        background-repeat: repeat-x;}
div#content h2 {
    font-size: 2.1em!important;
}
    div#content h3, div#content h4 {
    font-weight: normal!important;
}
div#content h3 {
    font-size: 1.65em!important;
}

div#content h4 {
    font-size: 1.25em;
}
    #toc, .toc, .mw-warning {
    font-size: 105%;
}
table {border:0px!important;}
}
