css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("dobrochan.ru"), domain("dobrochan.org"), domain("dobrochan.com") {
    div.thread {
        margin-left: 10em; 
    }
    div.de-create-thread {
        text-align: left;
        margin-left: 10em;
    }
    div.message {
        max-width: 38em;
        font-size: 14pt;
        word-wrap: break-word;
    }
    table.post {
        margin-top: 0.5em; 
        margin-bottom: 0.5em; 
    }
}
