css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://mlb.mlb.com/mlb/fantasy/bts/") {

    html, body, #bodyWrap, #top-content-container, .main-content,
    .content-wrapper [class$="active"], .ui-dialog-buttonpane,
    #bts-players-grid, .data_grid td, #account-nav .drop {
        background: #171717 !important;
    }

    .main-tabs li, .bts-list-table th, #follow-gameday-btn,
    .select-action, .remove-action, .picks-tabs a,
    .leaders-table th, .bam-button, .ui-button, .ui-menu-item a,
    .profile-players-list-view th, .data_grid thead th {
        background: #232323 !important;
        color: #888 !important;
        text-shadow: none !important;
    }

    .main-tabs [class$="active"], .picks-tabs .active a {
        background: #363636 !important;
        color: #00ADEE !important;
    }

    .tab-contents, .main-picks-table td, .main-picks-table tr,
    .content-wrapper, .content-container, .ui-widget-content {
        background: #0f0f0f !important;
        color: #AAA !important;
    }

    .content-wrapper th, .content-wrapper td,
    .sort-link, .profile-table th, .data_grid td,
    .leaders-table .player-name {
        color: #AAA !important;
    }

    .leaders-table .arrow {
        background: url("data:image/png;base64,") !important;
    }

    .date.today, .ui-button:hover,
    .content-container p a,
    .content-container a[href^="#"], #bts-faq-link,
    #account-nav .drop li a:hover {
            color: #00ADEE !important;
    }

    .date.today .label, .dd-logo, .app-store, .droid-store {display: none !important;}

    #bts-sub-header .streak {
        top: 1px !important;
        left: 530px !important;
        background: #1a1a1a !important;
        border-radius: 50% !important;
        border: 1px dotted #888 !important;
    }

    #bts-sub-header .streak span {
        top: 3px !important;
        left: 0 !important;
        height: 68px !important;
        width: 68px !important;
        border-radius: 50% !important;
        font-size: 4em !important;
        color: #00ADEE !important;
    }

    #bts-content {
        background: #171717 !important;
        border: 1px dotted #AAA !important;
    }

    #teams-list, .ui-dialog-titlebar, #account-nav .drop li:hover {background: #232323 !important;}

   .teams li a, .profile-players-list-view td {color: #CCC !important;}

    .teams li a:hover {
        background-color: #171717 !important;
        color: #00ADEE !important;
    }

    .teams > li:hover, .teams > li.active {
        padding: 2px !important;
        border: none !important;
        outline: 1px dashed rgba(102, 102, 102, 0.5) !important;
    }

    #top-content *, .ui-dialog-titlebar span {color: #888 !important;}

    #top-content-container {
        width: 100% !important;
        position: fixed !important;
    }

    #bts-sub-header, #mc h2 {padding-top: 37px !important;}

    a[name="page_top"] {padding-top: 25px !important;}

    .prizes-list-overlay li, .badges-list-overlay  li {background: #363636 !important;}

    #bts-players-grid {
        height: 576px !important;
        border: 2px solid #444 !important;
    }

    .main-picks-table .streak * {font-size: 3em !important;}

    .main-picks-table .streak .red {color: #EE4100 !important;}

    .main-picks-table .streak .green {color: #ADEE00 !important;}

    ::-moz-selection {background: #00678E !important; color: #FFF !important}

    .leaders-table .username {font-size: 1.9em !important;}

    #unclaimed-prize canvas {display: none !important;}

}


@-moz-document url-prefix("http://mlb.mlb.com/mlb/fantasy/bts/y2014/?content=rules"),
url-prefix("http://mlb.mlb.com/mlb/fantasy/bts/y2014/index.jsp?content=prizes") {
    #contentWrap {width: 1200px !important;}
    .content-container {
        font-size: 1.4em !important;
    }
}
