css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://[^/]*mlb\\.com/mlb/gameday/.*") {

    *, *:before, *:after {text-shadow: none !important;}

    #main {padding: 0px !important;}

    #gameday-header-ad-container, #tickerContainer, #mlbfooter
    {
        display: none !important;
    }

    #gdHdr {height: 69px !important;}

    #homeLink {top: 2px !important;}

    #scoreboardDateNavContainer {top: 10px !important;}

    #audio, #video {margin-top: 2px !important;}

    .audioPlayerContent
    {
        top: 31px !important;
        z-index: 9999 !important;
    }

    .audioPlayerContent,
    #audioCatalog ul,
    #audioCatalog .audioLinkContainer
    {
        cursor: default !important;
    }

    #audioPlayerControls *,
    #audioCatalog input.audioLink,
    #audioCatalog .audioLinkContainer label
    {
        cursor: pointer !important;
    }

    #audioCatalog {padding-bottom: 0 !important;}

    #top-content-container {margin-top: 0 !important;}

    #account-nav .drop {background: #171717 !important;}

    #account-nav .drop li a, .sbMini .game .winner
    {
        color: #AAA !important;
    }

    #account-nav .drop li a:hover,
    .monthLink a,
    #monthDays a,
    #dateSelector #subLinks a,
    .gameday-view a,
    .gameday-view a *
    {
        color: #00ADEE !important;
    }

    #account-nav .drop li:hover {background: #232323 !important;}

    #scoreboardContainer {margin-top: -14px !important;}

    .sbMini .game .upArrow {border-bottom: 8px solid #EE4100 !important;}

    .sbMini .game .dnArrow {border-top: 8px solid #ADEE00 !important;}

    .sbMini .sbNav
    {
       background-color: #2a2a2a !important;
       opacity: 0.2 !important;
    }

    .sbMini .sbNav:hover {opacity: 1 !important;}

    #monthNav,
    #monthDays .blank,
    #wrap-bottom-bg,
    #gameday-view-lineups * th,
    #gameday-view-box * th
    {
        background-color: #0f0f0f !important;
    }

    #games li,
    #plays li dd,
    #plays li dd *
    {
        background-color: #1a1a1a !important;
    }

    #games [class*=highlightedGame] {background-color: #2f2f2f !important;}

    .sbMini .gameText {color: #999 !important;}

    #games [class*=highlightedGame] .selectedText {color: #EEE !important;}

    #games li:last-child {border: none !important;}

    .curMonth,
    #gameday-view-lineups * *:not(a),
    #gameday-view-box * *:not(a),
    .goog-inline-block .fyre-button-right-inner-box
    {
        color: #CCC !important;
    }

    #dateSelector, #dateSelector #subLinks, .sbMini #games,
    [id^="btn-gameday-tab-"]:not([id$=classic]):not([id$=gameday])
    {
        background: #000 !important;
        border-color: #AAA !important;
    }

    #monthDays .day,
    .linescore .runs,
    #gameday-view-lineups * tr,
    #gameday-view-box * tr
    {
        background-color: #212121 !important;
    }

    [id^="btn-gameday-tab-"].gameday-view-tab-on
    {
        background: #212121 !important;
        outline: none !important;
    }

    .gameday-view, #plays li *,
    .fyre-box-wrapper
    {
        background-color: #0f0f0f !important;
        color: #CCC !important;
    }

    #wrap-video p,
    .article-video p
    {
        color: #636363 !important;
    }

    #wrap-blurb p {font-size: 1.1em !important;}

    .linescore tbody tr th,
    .linescore tbody tr td
    {
        color: #BBB !important;
    }

    .preview,
    .recap,
    .preview-bg-1,
    .preview-bg-2
    {
        background: none !important;
    }

    #gameday-view-box [class*="stats"] {border-collapse: unset !important;}

    #gameday-view-box [class*="stats"] td,
    #plays td
    {
        border-bottom: 1px dotted #777 !important;
    }

    #gameday-view-box [class*="totals"] td {border-bottom: none !important;}

    #gameday-view-lineups * tr:hover,
    #gameday-view-box * tr:hover:not([class="away"]):not([class="home"])
    {
        background-color: #000 !important;
    }

    #plays li h3:hover,
    #plays li.plays-atbat:hover,
    #plays li.plays-atbat:hover dt,
    #plays li.plays-atbat:hover dt span
    {
        background-color: #001A24 !important;
    }

    #plays dd {border-color: #777 !important;}

    .fyre-box-list,
    .fyre-editor,
    .fyre-editor-toolbar .goog-toolbar > div
    {
        background: #1a1a1a !important;
    }

    .fyre-editor-field
    {
        background: #232323 !important;
        color: #FFF !important;
    }

    #gdContent
    {
        background: transparent !important;
        margin-bottom: 5px!important;
    }

    #gamestateNavContainer
    {
        margin-top: 0 !important;
        margin-bottom: 0 !important;
    }

    #gamedayMiniLinkContainer a
    {
        background: none !important;
        padding: 0 !important;
    }

}


@-moz-document regexp("https?://[^/]*mlb\\.com/shared/components/gameday/gdapp/.*") {

    *, *:before, *:after {text-shadow: none !important;}

    #pitch_by_pitch,
    .LinescoreViewController,
    .mini_linescore .embedded_linescore .fixed
    {
        background-color: #000 !important;
    }

    #pitch_by_pitch .player {opacity: 0.8 !important;}

    .CardViewController.pullTab > .contentWrapper
    {
        background-color: rgba(50, 50, 50, 0.45) !important;
    }

    .CardView-left.pullTab:after,
    .CardView-right.pullTab:after
    {
        background-color: #323232 !important;
        color: #888 !important;
        border: none !important;
        opacity: 0.75 !important;
    }

    .PitchByPitchViewController .matchup-status,
    .padded-view, #gdVideoContainer
    {
        background-color: #0a0a0a !important;
        color: #CCC !important;
    }

    .PitchByPitchViewController .matchup-status,
    .PitchByPitchViewController .event_info,
    .PitchByPitchViewController #matchup,
    .pitches_holder .pitch, .pitch .pitch_info,
    .PitchByPitchViewController .play,
    .embedded_linescore,
    #boxscore .batting tr.totals,
    #boxscore .pitching tr.totals,
    #boxscore .bullpen tr:last-child,
    #boxscore .bench tr:last-child,
    #plays header,
    #plays section .table_row
    {
        border-color: #636363 !important;
    }

    .PitchByPitchViewController .player-pitcher,
    .PitchByPitchViewController .player-batter,
    #boxscore .batting thead tr,
    #boxscore .pitching thead tr,
    #boxscore .bullpen thead tr,
    #boxscore .bench thead tr,
    #plays header
    {
        background-color: #1a1a1a !important;
        color: #AAA !important;
    }

    .PitchByPitchViewController .title_extended
    {
        color: #BBB !important;
        font-weight: bold !important;
        font-family: sans !important;
        padding-bottom: 2px !important;
    }

    .gd-info-event,
    .embedded_linescore .first,
    .secondary .tab-bar li.active a,
    #boxscore.padded-view .box a,
    #field_view .info.info-player a,
    .linescore .first,
    .mini_linescore * .info a,
    .mini_linescore .upnext .player .info span
    {
        color: #00ADEE !important;
    }

    .PitchByPitchViewController .player-pitcher .stats,
    .PitchByPitchViewController .player-batter .stats,
    .secondary .tab-bar li a,
    .linescore thead,
    .mini_linescore .upnext .player .info .stats
    {
        color: #888 !important;
    }

    .pitch
    {
        background-color: #2a2a2a !important;
        color: #888 !important;
    }

    .pitchNumber {margin-right: 5px !important;}

    .pitchNumber [class^="pitch-"]
    {
        font-family: sans !important;
        top: 24% !important;
        height: 27px !important;
        width: 27px !important;
        line-height: 27px !important;
    }

    .pitch .pitch_info .call-description
    {
        color: #CCC !important;
        font-family: sans !important;
    }

    .play, #plays .detail
    {
        background-color: #101010 !important;
        color: #AAA !important;
        padding-right: 5px !important;
    }

    .PitchByPitchViewController .player-pitcher a,
    .PitchByPitchViewController .player-batter a,
    .pitches_holder .pitchesCount
    {
        color: #00ADEE !important;
        font-family: sans !important;
        font-weight: bold !important;
    }

    .pitches_holder .pitch:last-child .pitchesCount {padding-top: 27px !important;}

    .pitches_holder .pitchesCount
    {
        line-height: 1em !important;
        padding-top: 25px !important;
    }

    .gd-info-event, .gd-info-scoring
    {
        background-color: #1a1a1a !important;
        border: 1px dotted #CCC !important;
    }

    .info-outs {color: #EE4100 !important;}

    .gd-info-scoring
    {
        color: #ADEE00 !important;
    }

    div.triangle:before,
    .jingleContainer
    {
        display: none !important;
    }

    div.triangle {border-color: #555 transparent -moz-use-text-color !important;}

    .ScrollPanelView .ScrollPanelView-nav li, .secondary .tab-bar li
    {
        background-color: #232323 !important;
        color: #888 !important;
        border: 1px solid #636363 !important;
        cursor: pointer !important;
    }

    .ScrollPanelView .ScrollPanelView-nav li.active,
    .secondary .tab-bar li.active
    {
        background-color: #2a2a2a !important;
        color: #00ADEE !important;
    }

    .embedded_linescore .active:after {background-color: rgba(0, 0, 0, 0.25) !important;}

    .secondary.scores * {color: #EEE !important;}

    #boxscore .box .active
    {
        background: linear-gradient(#323232, #111, #0a0a0a, #0a0a0a, #111, #323232) !important;
        color: #FFF !important;
        outline: 1px solid #636363 !important;
    }

    #boxscore .batting tr.totals,
    #boxscore .pitching tr.totals
    {
        border-top: 1px solid #636363 !important;
    }

    .inning_nav h5 {cursor: pointer !important;}

    .inning_nav .active h5
    {
        background: none !important;
        color: #00ADEE !important;
        border: 1px solid #636363 !important;
    }

    #videos #playlist *,
    #field_view h1,
    .linescore tbody,
    .bso .count > .title,
    .mini_linescore * .title
    {
        color: #CCC !important;
    }

    #videos #playlist .selected {background-color: #2a2a2a !important;}

    #videos #playlist .play {background-color: transparent !important;}

    #field_view .fielder a
    {
        background-color: rgba(0, 0, 0, 0.75) !important;
        color: #00ADEE !important;
        border-radius: 2px !important;
    }

    .mini_linescore .runs-column-background-faker
    {
        background-color: #000 !important;
        border: 1px dotted #00ADEE !important;
    }

    .embedded_linescore .inning_button {border-bottom-color: transparent !important;}

    .embedded_linescore.previous .inning_button.prev_button,
    .embedded_linescore.previous .inning_button.next_button
    {
        background-color: #232323 !important;
        border-color: #636363 !important;
        opacity: 0.3 !important;
        cursor: pointer !important;
    }

    .embedded_linescore.previous .inning_button.prev_button:hover,
    .embedded_linescore.previous .inning_button.next_button:hover
    {
        opacity: 1 !important;
    }

    .CardViewController .close {
        background-image: url("data:image/jpeg;base64,") !important;
        opacity: 0.15 !important;
        border: 1px #CCC solid !important;
    }

    .CardViewController .close:hover {opacity: 1 !important;}

}


@-moz-document regexp("https?://[^/]*mlb\\.com/mlb/gameday/responsive/player/wired.*") {

    body
    {
        background-color: #0a0a0a !important;
        color: #AAA !important;
    }

    #player_game, #player_summary,
    #player_stats, #playerSplits
    {
        margin-right: 10px !important;
    }

    .playerData .player_summary td {padding: 1em 0 1em 0.6em !important;}

    .playerData .player_summary .name
    {
        color: #00ADEE !important;
        margin: 0.75em 0 0.25em 0 !important;
    }

    .playerData .player_bio li,
    .playerData .player_performance
    {
        color: #AAA !important;
    }

    .playerData .player_bio li strong,
    .playerData td
    {
        color: #CCC !important;
    }

    .playerData .player_bio li:nth-child(-n+4) {font-size: 1.15em !important;}

    .playerData .player_bio li:nth-last-child(-n+3) {font-size: 0.85em !important;}

    .playerData .secondary li
    {
        background-color: #0a0a0a !important;
        border: none !important;
    }

    .playerData .secondary li:last-of-type {border-right: 1px solid #636363 !important;}

    #player_game.playerData nav.secondary li#nav-player-game a,
    #player_summary.playerData nav.secondary li#nav-player-index a,
    #player_stats.playerData nav.secondary li#nav-player-stats a,
    #playerSplits.playerData nav.secondary li#nav-player-splits a
    {
        background-color: #2a2a2a !important;
        color: #00ADEE !important;
    }

    .playerData .secondary li a
    {
        display: block !important;
        background-color: #232323 !important;
        color: #888 !important;
        border: 1px solid #636363 !important;
    }

    .playerData h2
    {
        background-color: #0a0a0a !important;
        color: #888 !important;
        border-color: #636363 !important;
    }

    .playerData h2 {margin-top: 20px !important;}

    .playerData h2 + .player_performance {padding-bottom: 15px !important;}

    .playerData h3
    {
        color: #636363 !important;
        border-bottom: 1px solid #636363 !important;
    }

    .playerData th
    {
        background-color: #1a1a1a !important;
        color: #AAA !important;
    }

    .playerData tr {border-color: #636363 !important;}

    .playerData .player-stats-summary {border-bottom: none !important;}

    .playerData .table-container table:first-of-type
    {
        background-color: #0a0a0a !important;
        box-shadow: none !important;
        border-collapse: collapse !important;
        border-right: 1px dotted #636363 !important;
    }

    .playerData .standard-select .select-style
    {
        background-color: #323232 !important;
        border-color: #636363 !important;
    }

    .playerData .standard-select select {color: #00ADEE !important;}

}


@-moz-document regexp("https?://[^/]*mlb\\.com/shared/components/gameday/gamefeed/.*") {

    #game-feed .row
    {
        background-color: #0a0a0a !important;
        color: #CCC !important;
        border-color: #636363 !important;
    }

    #game-feed .inning-top
    {
        background-color: #1a1a1a !important;
        border-color: #636363 !important;
    }

    #game-feed .inning-top p {color: #AAA !important;}

    #game-feed .btn-default
    {
        color: #00ADEE !important;
        background-color: #1a1a1a !important;
        border: 1px dotted #CCC !important;
    }

    #game-feed .score
    {
        color: #ADEE00 !important;
        text-shadow: none !important;
    }

}
