@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reference.com"), domain("thesaurus.com"), domain("dictionary.com") {

    body {background: #0f0f0f !important;}

    input {background-color: #0f0f0f !important;}

    #cwrp, #dmain div, #hdr, #main, #mlrs,
    #nfotr, #nfr, #profpap, #qtts, #qun,
    #qtil, #qotc, #relatedSearch, #tweetyer,
    #sidebar .gray-box, #studpap, #topwrapper,
    #wrapper, #contouter, #de, #dmain, #main,
    #sidebar, .sidebar-holder,.sidebar-frame,
    .gray-box, #prev-words, .shd_hdr, #nfotr,
    #nfr, #dmain .tlc, #dmain .tla {
        background: #1a1a1a !important;
    }

    #sidebar, .sidebar-holder, .sidebar-frame, #seasonWrp,
    #content .title-reference, .title-word, .aside .social-box li,
    .popular-title {
        background-image: none !important;
    }

    #main, * span, .hd, #exsntHdg, .rwh, .qhdr, .dicTl, .aside {color: #BBB !important;}

    h1, h2, h3, input {color: #FFF !important;}

    a, .h3-carousel-style .block {color: #00ADEE !important;}

    a:hover {color: #EE4100 !important;}

    a:visited {color: #35D4AF !important;}

    #slider, #stickad, #adclose, #top, .banner-ad, .ftsp, #Dash_0, 
    #lr_mob, #Dash_1, #Dash_2, #Dash_3, #Dash_4, #Dash_5, #wrapserp,
    .bottomAd, ._vtad, .banner-holder, .banner, .ggadwrp, #six, #rg,
    #s1, .tpad, .lr, .btbanner, .ad, .bannerr, .csrright, .sykscraper-ad,
    .box-ad, #atlasAdDivGame, #buyads, .zigzag, #adcontainer1, .sub-nav li,
    [src="http://partner.googleadservices.com/gampad/google_service.js"] {
        display: none !important;
    }

}
