style_setting_options.value:
.html5-play-progress, .ytp-play-progress {
	background: url("http://i.imgur.com/EbuQBVO.png") repeat-x ! important;
}
.html5-scrubber-button, .ytp-scrubber-button {
	background: url("http://i.imgur.com/sR2lxuX.gif") ! important;
	background-repeat: no-repeat ! important;
	width: 31px ! important;
	height: 40px ! important;
	border: none ! important;
	margin-left: -16px ! important;
	margin-top: -25px ! important;
}
.ytp-volume-slider-foreground:before {
	background: #0278d0 ! important;
	background: -moz-linear-gradient(left, #0278d0 0%, #0278d0 100%) ! important;
	background: -webkit-gradient(linear, left top, right top, color-stop(0%,#0278d0), color-stop(100%,#0278d0)) ! important;
	background: -webkit-linear-gradient(left, #0278d0 0%,#0278d0 100%) !important;
	background: -o-linear-gradient(left, #0278d0 0%,#0278d0 100%) ! important;
	background: -ms-linear-gradient(left, #0278d0 0%,#0278d0 100%) ! important;
	background: linear-gradient(to right, #0278d0 0%,#0278d0 100%) ! important;
}

.html5-play-progress, .ytp-play-progress {
	background: url("http://i.imgur.com/R3uGDHL.png") repeat-x ! important;
}
.html5-scrubber-button, .ytp-scrubber-button {
	background: url("http://i.imgur.com/3HNuxg7.gif") ! important;
	background-repeat: no-repeat ! important;
	width: 36px ! important;
	height: 38px ! important;
	border: none ! important;
	margin-left: -16px ! important;
	margin-top: -25px ! important;
}
.ytp-volume-slider-foreground:before {
	background: #0278d0 ! important;
	background: -moz-linear-gradient(left, #e88809 0%, #e88809 100%) ! important;
	background: -webkit-gradient(linear, left top, right top, color-stop(0%,#e88809), color-stop(100%,#e88809)) ! important;
	background: -webkit-linear-gradient(left, #e88809 0%,#e88809 100%) !important;
	background: -o-linear-gradient(left, #e88809 0%,#e88809 100%) ! important;
	background: -ms-linear-gradient(left, #e88809 0%,#e88809 100%) ! important;
	background: linear-gradient(to right, #e88809 0%,#e88809 100%) ! important;
}

.html5-scrubber-button, .ytp-scrubber-button {
	background: url("https://i.imgur.com/96Q0DBf.jpg") ! important;
	background-repeat: no-repeat ! important;
	width: 36px ! important;
	height: 36px ! important;
	border: none ! important;
	margin-left: -16px ! important;
	margin-top: -22px ! important;
}
