css:
@-moz-document url-prefix("https://eksisozluk.com/"){ 

/* 

BAGGGGZI ULKER LINKLER ve ACIKLAMALARI
(asagidaki bg'lar orijinal theme'de hangi amacla kullanildiklarina gore listeleniyor.)
DEFAULT BG: http://i.imgur.com/pSM3l3W.jpg
ALTERNATE BG: http://i.imgur.com/0OQozfq.jpg
BUTTON BG: http://i.imgur.com/71TIEik.jpg

*/

html.no-touch > body {
  padding-top: 65px;
  font: 10pt trebuchet ms , sans-serif;
  background-image: url(http://i.imgur.com/pSM3l3W.jpg);
  color: #000;
}

a:hover, .new-update {
background-color: #BA7663;
background-image: url(http://i.imgur.com/0OQozfq.jpg);
text-decoration: none;
}

a, .sub-title-menu>a, .sub-title-menu>div>a, .sub-title-menu>div>.dropdown>a {
color: #fff;
text-decoration: none;
}

#entry-list footer div.feedback>a, #entry-list footer div.options>a, #entry-list footer div.other>a, #entry-list footer .rate-options a {
color: #fff;
}

h1#title
{
background-color: #BA7663;
background-image: url(http://i.imgur.com/0OQozfq.jpg);
}

body>header {
background-image: url(http://i.imgur.com/pSM3l3W.jpg);
min-width: 300px;
}

nav#sub-navigation
{
  background-image: url(http://i.imgur.com/71TIEik.jpg); 
}

.ui-menu-item
{
background-image: url(http://i.imgur.com/pSM3l3W.jpg); 
}

#in-topic-search-options, .dropdown-menu, .share-dialog, .open
{
background-image: url(http://i.imgur.com/pSM3l3W.jpg); 
border: 2px solid #000;
}

.channel
{
background-image: url(http://i.imgur.com/0OQozfq.jpg);
}

#channel-follow-list li
{
border: 2px solid #000;
}

nav#sub-navigation ul li a, #user-badges li a, button.primary, input[type=submit].primary, input[type=button].primary, #top-bar input[type=submit].primary, #top-bar input[type=button].primary
{  
  color: #fff;
}

#user-badges li a, button.primary, submit.primary{
  background-image: url(http://i.imgur.com/71TIEik.jpg);
}

nav#sub-navigation ul li a:hover, .primary:hover, h3
{
background-image: url(http://i.imgur.com/0OQozfq.jpg);
}

ul.tabs
{
background-color:none;
}

}
