css:
@-moz-document domain("allegro.pl") {

.m-link-signal, .m-link-signal:active, .m-link-signal:focus, .m-link-signal:hover, .m-link-signal:visited {
    text-decoration: none;
    font-family: Arial;
    font-size: 12px !important;
}
.dropdown > ul > li, .vela .ma-layer > ul > li, .vela .dropdown > ul > li {
    font-size: 12px !important;
    line-height: 14px !important;
}
.myaccount-nav-menu__link { line-height: 11px; }
.myaccount-nav-menu__header {
    font-family: Arial;
    font-size: 15px !important;
    line-height: 15px !important;
}
.m-card, .m-link-signal, .m-tabs, .m-tabs__arrow, .m-tabs__content, .m-tabs__head, .m-tabs__nav, .m-tabs__strip, .m-tabs__tab, .m-type-header { font-family: Arial; }
.user-links-wrapper, .vela .user-links-wrapper {
    background: none repeat scroll 0% 0% #777 !important;
    height: 30px !important;
}

.user-links-wrapper .user-nav, .vela .user-links-wrapper .user-nav {
    background: none repeat scroll 0% 0% #777 !important;
    height: 30px !important;
}
.user-links-wrapper .user-nav > li > a, .vela .user-links-wrapper .user-nav > li > a {
    border-left: 1px solid #777 !important;
    background: none repeat scroll 0% 0% #777 !important;
    height: 30px !important;
}
.user-links-wrapper .user-nav > li:hover > a, .vela .user-links-wrapper .user-nav > li:hover > a {
    background: none repeat scroll 0% 0% #777 !important;

}
.user-links-wrapper .user-nav > li, .vela .user-links-wrapper .user-nav > li {
   border-left: 1px solid #777 !important;
   border-right: 1px solid #777 !important;

}
.user-links-wrapper .user-nav, .vela .user-links-wrapper .user-nav {
    border-bottom: 1px solid #777 !important;
    height: 30px !important;
}
.user-nav > li:first-child, .vela .user-nav > li:first-child {
    border-left: 1px solid #777 !important;
}
.user-links-wrapper .user-nav > li.login, .vela .user-links-wrapper .user-nav > li.login  {
    border-right: 1px solid #777 !important;
}
.user-links-wrapper .user-nav > li.subservices-links.last, .vela .user-links-wrapper .user-nav > li.subservices-links.last {
    border-right: 1px solid #777 !important;
}
.vela .user-links-wrapper .user-nav > li:last-child, .vela .user-links-wrapper .user-nav > li.login {
    border-right: 1px solid #777 !important;
}
    
.ma-layer > ul {
    background: #777 !important;
    margin-top: -5px !important;
}
.vela.header-namespace .user-links-wrapper { margin-top: -5px !important; }
DIV.user-links-wrapper UL#user-nav > LI.dropdown.ma-layer > A[href="http://allegro.pl/myaccount/"] > I
{         display: none !important; }

.m-tabs__nav {
    padding-bottom: 0px;
    margin: 0 0px -16px;
    line-height: 16px;
    font-size: 16px;
}
.m-tabs--multicard>.m-tabs__strip>.m-tabs__nav {
    padding-bottom: 0px; padding-top: -5px;
    margin-bottom: 0px; margin-top: -5px;
}
.m-card { max-width: 100% !important; }
.m-tabs { height: 30px !important; }
.m-tabs__strip { height: 30px !important; }
.m-tabs__tab { height: 30px !important; }
.vela.header-namespace .user-links-wrapper .user-nav>li.subservices-links.last a, .vela.header-namespace .user-links-wrapper .user-nav>li:first-child a, .vela.header-namespace .user-links-wrapper .user-nav>li:last-child a, .vela.header-namespace .user-links-wrapper .user-nav>li a {
    background-color: transparent;
    color: #fff !important;
    font-family: Arial !important;
}
.vela .wrapper-fluid {
	margin-top: -5px !important;
}
}
