css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mlbtraderumors.com") {

.headst a { border: 1px solid #444444 !important; }
.headst a:hover {
  color: #003366 !important;
  border: 1px solid #003366 !important;
}
.headst a:first-child + a:hover {
  color: #fbb !important;
  background-color: red !important;
  text-decoration: line-through !important;
}

.foot { font-style: italic !important; }
.foot a { font-style: normal !important; }
.foot a:first-child,
.foot a:first-child + a {
  font-weight: bold !important;
  padding: 2px !important;
}

.comment-footer a:first-child {
  font-weight: bold !important;
}

#middle form {
  height: 0 !important;
  visibility: hidden !important;
}

}
