css:
@-moz-document domain("www.exim.org") {
#inner {
  max-width: 100% !important;
  width: 95% !important;
  font-family: Lato, 'Helvetica Neue', Helvetica, Arial, sans-serif;
}
.left_bar {
  display: none !important;
}
.right_bar {
  display: none !important;
}
#nav_float, #nav_flow {
  background: #2c3e50 !important;
  text-decoration: none !important;
  padding-top: 19.5px !important;
  padding-bottom: 19.5px !important;
  margin-top: 0px !important;
  opacity: 1 !important;
}
h2 > a {
  text-decoration: none !important;
}
#header {
  display: none !important;
  top: 0 !important;
}

.search_field_container, li[class="img"] {
  display: none !important;
}
#header, #nav_flow {
  background: #2c3e50 !important;
  opacity: 1 !important;
}
#header a, #nav_flow a, #nav_float a {
  color: #fff !important;
  text-decoration: none !important;
  font-family: Lato, 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-size: 15px !important;
}
#header  a:hover, #nav_flow a:hover, #nav_float a:hover {
  color: #149c82 !important;
}
.docbook_literallayout {
    display: block !important;
  padding: 10px !important;
  margin: 0 0 10.5px !important;
  font-size: 14px !important;
  line-height: 1.42857143 !important;
  word-break: break-all !important;
  word-wrap: break-word !important;
  color: #7b8a8b !important;
  background-color: #ecf0f1 !important;
  border: 1px solid #cccccc !important;
  border-radius: 4px !important;
}
.docbook_literallayout pre {
    padding: 0 !important;
  font-size: inherit !important;
  color: inherit !important;
  white-space: pre-wrap !important;
  background-color: transparent !important;
  border-radius: 0 !important;
  margin-bottom: 0 !important;
}
h3,h2,h1 {
  margin-top: 21px !important;
  margin-bottom: 10.5px !important;
  font-family: "Lato", "Helvetica Neue", Helvetica, Arial, sans-serif !important;
  font-weight: 400 !important;
  line-height: 1.1 !important;
  color: #2c3e50 !important;
}
#footer {
  display: none !important;
}
code,
kbd,
pre,
samp {
  font-family: Menlo, Monaco, Consolas, "Courier New", monospace !important;
}
code {
  padding: 2px 4px !important;
  font-size: 90% !important;
  color: #c7254e !important;
  background-color: #f9f2f4 !important;
  white-space: nowrap !important;
  border-radius: 4px !important;
}
}
