css:
@-moz-document domain("lua-users.org") {

body {
  font-size: 16px;
  line-height: 1.5;
  font-family: "Segoe UI", Frutiger, "Frutiger Linotype", "Dejavu Sans", "Helvetica Neue", Arial, sans-serif;
  color: #333 !important;
  width: 800px;
  margin: 2em auto;
}

h1, h2, h3, h4 {
  font-family: Verdana, Geneva, sans-serif;
  text-rendering: optimizelegibility;
  color: #404040;
}

  h1 {
    font-size: 2rem;
  }

  h2 {
    font-size: 1.75rem;
  }

  h3 {
    font-size: 1.5rem;
  }

  h4 {
    font-size: 1.25rem;
  }

a:link {
  color: #4183c4 !important;
  text-decoration: none;
}

  a:visited {
    color: #6e1d6c !important;
  }

  a:hover,a:active {
    text-decoration: underline;
  }

hr {
  background-image: -webkit-gradient(linear, 0% 50%, 100% 50%, color-stop(0%, #fff), color-stop(50%, #cccccc), color-stop(100%, #ffffff));
  background-image: -webkit-linear-gradient(left center, #fff, #c4c4c4, #fff);
  background-image: -moz-linear-gradient(left center, #fff, #c4c4c4, #fff);
  background-image: -o-linear-gradient(left center, #fff, #c4c4c4, #fff);
  background-image: linear-gradient(left center, #fff, #c4c4c4, #fff);
  border: none;
  content: "";
  display: block;
  height: 1px;
  margin: 2em auto;
}

pre, code {
  font-family: Consolas, monaco, monospace;
}

code {
  color: #76a60f;
}

pre {
  padding-left: 1em;
  border-left: 4px solid #e8e8e8;
  color: #555;
}

}
