css:
/* Update at https://userstyles.org/styles/102050/dark-youtube-nights */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.youtube.com") {
    a {
        color: #268;
    }
    body {
        background-color: #111;
    }
    body#help {
        background-color: transparent;
    }
    
    /* Create > */
    #create-channel-identity-lb .yt-dialog-base .yt-dialog-header {
        border-color: #555;
    }
    .create-gplus-footer {
        border-color: #555;
    }
    .create-gplus-setup-page .pane-divider {
        border-color: #555;
    }
    /* < Create */
    
    /* Upload > */
    .upload-thumb {
        border: none;
    }
    .item-title {
        color: #777;
    }
    .upload-item {
        border-color: #555;
    }
    .site-center-aligned .yt-card.upload-item {
        background-color: #222;
    }
    .progress-bar-background {
        border-color: #555;
    }
    .progress-bar-uploading .progress-bar-progress {
        background: #333;
        border-color: #555;
    } 
    .progress-bar-processing .progress-bar-progress {
        background: #444;
        border: 1px solid #555;
        border-right-width: 3px;
    }
    .progress-bar-text,
    .progress-bar-text-done,
    .progress-bar-text-upload-queued,    
    .upload-time-remaining {
        color: #777;
    }  
    .item-cancel, .item-cancel:hover {
        border: 1px solid #555;
    }
    .basic-info-tab .custom-thumb-area {
        border-left-color: #555;
    }
    .metadata-thumbnail-chooser h3 {
        color: #777;
    }
    .thumb-placeholder {
        color: #555;
    }
    .thumbnail-overlay {
        border-color: #222;
    }
    .basic-info-tab .selectable-thumb-selected {
        border-color: #777;
    }
    .tabs .tab-header.selected a,
    .tabs .tab-header a:hover,
    .tabs .tab-header:hover a,
    .tabs .tab-header a:focus {
        color: #999;
    }
    .metadata-tab h3,
    .metadata-tab h4 {
        color: #777;
    }
    .metadata-tab {
        color: #666;
    }
    .translation-editor-header .translation-editor-column-half,
    .translation-editor-header .translation-editor-column-divider {
        border-color: #555;
    }
    .translation-editor-original-input,
    .translation-editor-original-input:hover,
    .translation-editor-original-input:focus {
        background-color: #333 !important;
        border-color: #555 !important;
        color: #888 !important;
    }
        
    /* < Upload */

    /* Search box > */
    #masthead-search-terms {
        background-color: #333;
    }
    #masthead-search-term {
        color: #999;
    }
    .masthead-search-terms-border {
        border-color: #555;
        box-shadow: 0 1px 2px #444 inset;
    }
    .gssb_e {
        border-color: #555;
    }
    .gssb_m {
        background-color: #333;
    }
    .gspr_a {
        color: #BBB;
    }
    .gssb_i td {
        background-color: #555;
    }
    .gsq_a span {
        color: #777;
    }
    a.gssb_j {
        color: #BBB;
    }
    .gsq_a span[style] {
        color: #BBB !important;
    }
    /* < Search box */

    /* Ticker (information bar at the top) > */
    #ticker {
        background-color: #3D4E6B;
        background-image: linear-gradient(to bottom, #49586B 0px, #3D4E6B 100%);
    }
    /* < Ticker */

    /* Alerts > */
    .yt-alert-default.yt-alert-success,
    .yt-alert-actionable.yt-alert-success,
    .yt-alert-naked.yt-alert-success .yt-alert-icon,
    .yt-alert-small.yt-alert-success {
        background-color: #342;
    }
    .yt-alert-default.yt-alert-info,
    .yt-alert-actionable.yt-alert-info,
    .yt-alert-naked.yt-alert-info .yt-alert-icon,
    .yt-alert-small.yt-alert-info {
        border-color: #3D4E6B;
        background-color: #3D4E6B;
    }
    .yt-alert-panel {
        background-color: #222;
        color: #999;
    }
    .yt-alert-naked .yt-alert-content {
        color: #555;
    }
    /* < Alert */

    /* Header > */
    #yt-masthead-container {
        background-color: #111;
        border-color: #555;
    }
    #yt-masthead-user-displayname {
        color: #AAA;
    }
    #masthead-expanded-container {
        background-color: #111;
        border-color: #555;
    }
    #masthead-appbar {
        background-color: #111;
        border-color: #555;
    }
    #masthead-expanded .masthead-expanded-menu-header {
        color: #888;
    }
    #masthead-expanded-menu-account-info p {
        color: #AAA;
    }
    #masthead-positioner:hover #appbar-guide-button {
        background-image: linear-gradient(to top, #222 0px, #444 100%);
        border-color: #555;
    }
    .yt-uix-clickcard-card-border, .yt-uix-hovercard-card-border {
        background-color: #181818;
        border-color: #555;
    }
    .yt-masthead-picker-name {
        color: #AAA;
    }
    .yt-masthead-picker-account-subtitle {
        color: #777;
    }
    .yt-masthead-account-picker.yt-uix-clickcard-card-content {
        color: #666;
    }
    .yt-masthead-account-picker-option {
        background-color: #282828;
    }
    .yt-masthead-account-picker-option:hover {
        background-color: #333;
    }
    .yt-masthead-account-picker-option .yt-masthead-picker-name {
        color: #BBB;
    }
    .yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header,
    .yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header {
        background-color: #383838;
    }
    .yt-uix-clickcard-card-reverse .yt-uix-card-body-arrow-vertical,
    .yt-uix-hovercard-card-reverse .yt-uix-card-body-arrow-vertical {
        border-bottom-color: #181818;
    }
    .yt-uix-clickcard-card-reverse .yt-uix-card-border-arrow-vertical,
    .yt-uix-hovercard-card-reverse .yt-uix-card-border-arrow-vertical {
        border-bottom-color: #555;
    }
    .yt-uix-clickcard-card.yt-masthead-multilogin .yt-uix-card-body-arrow {
        border-bottom-color: #383838;
    }
    .yt-masthead-picker-footer {
        background-color: #111;
    }
    .yt-uix-card-border-arrow-vertical {
        border-top-color: #555;
        border-bottom-color: #555;
    }
    .yt-uix-card-body-arrow-vertical {
        border-top-color: #181818;
        border-bottom-color: #181818;
    }
    .yt-uix-card-border-arrow-horizontal {
        border-left-color: #555;
        border-right-color: #555;
    }
    .yt-uix-card-body-arrow-horizontal {
        border-left-color: #181818;
        border-right-color: #181818;
    }
    .yt-default p, p.yt {
        color: #666;
    }
    #yt-masthead-user #upload-btn {
        opacity: 1;
    }
    #yt-masthead-user #sb-button-notify,
    #yt-masthead-user #yt-masthead-notifications-button,
    #yt-masthead-user #upload-btn {
        background-color: #282828;
        border: 1px solid #555;
        margin-right: 15px;
    }
    #yt-masthead-user #sb-button-notify:hover,
    #yt-masthead-user #yt-masthead-notifications-button:hover,
    #yt-masthead-user #upload-btn:hover {
        background-color: #383838;
        border-color: #777;
    }
    #yt-masthead-user #sb-button-notify:focus,
    #yt-masthead-user #yt-masthead-notifications-button:focus,
    #yt-masthead-user #upload-btn:focus {
        box-shadow: 0 0 0 2px rgba(212, 212, 212, 0.4);
    }
    #yt-masthead-notifications-title {
        color: #777;
    }
    #yt-masthead-notifications-content {
        background-color: #222;
    }
    #yt-masthead-notifications-content .item-section > li > .yt-lockup-tile,
    #yt-masthead-notifications-content .yt-ui-ellipsis {
        background-color: #222;
        color: #555;
    }
    #yt-masthead-notifications-content .yt-lockup-byline {
        color: #777;
    }
    #yt-masthead-notifications-content .item-section > li > .yt-lockup-tile:hover,
    #yt-masthead-notifications-content .yt-lockup-tile:hover .yt-ui-ellipsis {
        background-color: #333;
    }
    #yt-masthead-notifications-content .item-section > li > .yt-lockup-tile {
        border-bottom-color: #555;
    }
    /* < Header */

    /* Popups > */
    #dialog {
        background-color: #222;
        border-color: #585858 #585858 #484848;
        box-shadow: 0 4px 16px rgba(128, 128, 128, 0.3);
    }
    .header {
        background-color: #383838;
        border-color: #555;
    }
    .search-box {
        background-color: #333;
        border-color: #555;
        box-shadow: 0 1px 2px #444 inset;
    }
    .search-panel .suggestions-container {
        background-color: #222;
    }
    .search-panel .title {
        color: #999;
    }
    .yt-dialog-fg {
        background-color: #222;
        border-color: #555;
    }
    #identity-prompt-lb .page-specialized {
        background-color: #222;
    }
    #identity-prompt-lb .specialized-identity-prompt-account-item.main {
        background-color: #444;
        color: #AAA;
    }
    #identity-prompt-lb .specialized-identity-prompt-account-item.main:hover {
        background-color: #555;
        border-color: #777;
        color: #CCC;
    }
    #identity-prompt-lb .specialized-identity-prompt-account-item {
        border-color: #555;
    }
    #identity-prompt-lb .specialized-identity-prompt-account-item:hover {
        background-color: #333;
        color: #888;
    }
    .no-focus-outline .yt-dialog-fg:focus {
        background-color: #222;
        border-color: #555;
    }
    .yt-dialog-fg-content .yt-dialog-title,
    .yt-uix-overlay-fg-content .yt-dialog-title {
        color: #AAA;
    }
    .yt-dialog-fg-content,
    .yt-uix-overlay-fg-content {
        color: #666;
    }
    .yt-dialog-bg,
    .yt-uix-overlay-bg,
    #glass {
        background-color: #000;
    }
    .yt-dialog-working-overlay {
        background-color: #000;
        border-color: #000;
        border-bottom-width: 2px;
    }
    .yt-dialog-working-bubble {
        background-color: #222;
        border-color: #555;
    }
    .yt-uix-overlay-actions {
        background-color: #181818;
    }
    .yt-uix-overlay-primary .yt-dialog-header {
        background-color: #222;
    }
    .yt-uix-overlay-primary .yt-dialog-header h2 {
        color: #999;
    }
    .info-popup,
    .modal-dialog {
        background-color: #222;
        border-color: #555;
        box-shadow: 0 4px 16px #000;
    }
    .modal-dialog-title {
        background-color: #222;
        color: #999;
    }
    .modal-dialog {
        color: #777;
    }
    .modal-dialog-content {
        background-color: #222;
        color: #777;
    }
    .modal-dialog-buttons button,
    .modal-dialog-buttons button[disabled],
    .modal-dialog-buttons button[disabled]:hover,
    .modal-dialog-buttons button[disabled]:active,
    .modal-dialog-buttons button[disabled]:focus {
        background: #282828;
        background-color: #282828;
        border-color: #555;
        color: #888;
    }
    .modal-dialog-buttons button:hover {
        background: #383838;
        background-color: #383838;
        border-color: #777;
        color: #888;
    }
    .modal-dialog-buttons button:active {
        background: #484848;
        background-color: #484848;
        border-color: #777;
        color: #888;
    }
    .jfk-button-standard {
        background: linear-gradient(to bottom, #282828 0px, #222 100%);
        border-color: #555;
        color: #888;
    }
    .jfk-button-standard.jfk-button-hover,
    .jfk-button-standard.jfk-button-clear-outline.jfk-button-hover {
        background: linear-gradient(to bottom, #333 0px, #222 100%);
        border-color: #777;
        color: #888;
    }
    .jfk-button-standard.jfk-button-checked,
    .jfk-button-standard.jfk-button-clear-outline.jfk-button-checked {
        background: linear-gradient(to bottom, #181818 0px, #282828 100%);
        border-color: #777;
        color: #888;
    }
    #wizard {
        background: none;
    }
    #feedback-annotator div {
        border-color: #555;
    }
    #wizard textarea {
        background-color: #333;
        border-color: #555;
        color: #888;
    }
    #wizard textarea:hover {
        background-color: #363636;
        border-color: #666;
        color: #AAA;
    }
    #wizard textarea:focus {
        border-color: #777;
    }
    .goog-zippy-header:focus, .goog-zippy-header:hover {
        background-color: #333;
    }
    /* Send a message > */
    .yt-channel-msg-dialog .user-box {
        border-color: #555;
    }
    .yt-channel-msg-dialog .compose-message {
        background-color: #333;
        border-color: #555;
        color: #888;
    }
    .yt-channel-msg-dialog .compose-message:hover {
        background-color: #363636;
        border-color: #666;
        color: #AAA;
    }
    .yt-channel-msg-dialog .compose-message:focus {
        border-color: #777;
    }
    .yt-channel-msg-dialog .yt-dialog-footer {
        background-color: #181818;
    }
    /* < Send a message */
    /* < Popups */

    /* Sidebar left > */
    #appbar-guide-menu, .guide-flyout {
        background-color: #111;
    }
    .guide-pinned .guide-pinning-enabled #appbar-guide-menu {
        border-color: #555;
    }
    .guide-item {
        color: #777;
    }
    .guide-section-separator {
        border-color: #555;
    }
    .guide-flyout {
        border-color: #555;
    }
    .guide-header.signup-promo p {
        color: #777;
    }
    #guide-subscriptions-promo.default-promo {
        border-color: #555;
    }
    .guide-subscriptions-promo-tooltip {
        background-color: #259;
        border-color: #36A;
    }
    /* History > */
    #history-search .history-search-field {
        background-color: #333;
        border-color: #555;
        color: #888;
    }
    #history-search .history-search-field:hover {
        background-color: #363636;
        border-color: #666;
        color: #AAA;
    }
    #history-search .history-search-field:focus {
        border-color: #777;
    }
    .comment-history-block-header {
        border-color: #555;
    }
    .comment-history-entry-renderer-content {
        color: #555;
    }
    /* < History */
    /* < Sidebar left */
    
    /* Find channels > */
    .category-header .category-title {
        color: #888;
    }
    .yt-gb-shelf-main-content {
        border-color: #555;
        box-shadow: 0 1px 2px #555;
    }
    .yt-gb-shelf-main-content:hover {
        box-shadow: 0 2px 3px #555;
    }
    .yt-gb-shelf-paddle {
        background-color: #333;
        border-color: #555;
    }
    .yt-gb-preview-videos-title {
        color: #888;
    }
    .yt-gb-shelf-hero-content .description {
        color: #666;
    }
    /* < Find channels */

    /* Sidebar right > */
    .branded-page-related-channels h3 a,
    .branded-page-related-channels h3 {
        color: #888;
    }
    .branded-page-v2-secondary-col .branded-page-related-channels-see-more a {
        color: #666;
    }
    .related-channels-editor:hover {
        background-color: #222;
    }
    /* < Sidebar right */
    
    /* Live chat > */
    #chat-messages {
        background-color: #131313;
    }
    .yt-live-chat-item-list-renderer {
        background-color: #222;
    }
    .yt-live-chat-message-input-renderer-0 {
        background-color: #333;
    }
    .yt-live-chat-header-renderer-0 {
        color: white;
        background-color: #333;
    }
    .yt-live-chat-text-message-renderer-0 #author-name.yt-live-chat-text-message-renderer,
    .yt-live-chat-message-input-renderer-0 #author-name.yt-live-chat-message-input-renderer {
        color: #AAA;
    }
    .yt-live-chat-text-message-renderer-0 #message.yt-live-chat-text-message-renderer,
    .yt-live-chat-message-input-renderer-0 #count.yt-live-chat-message-input-renderer {
        color: #666;
    }
    .yt-live-chat-text-input-field-renderer-0 #label.yt-live-chat-text-input-field-renderer {
        color: #666;
    }
    .yt-live-chat-text-input-field-renderer-0 #input.yt-live-chat-text-input-field-renderer {
        color: #888;
    }
    .yt-live-chat-text-input-field-renderer-0 #unfocused.yt-live-chat-text-input-field-renderer {
        background-color: #555;
    }
    .yt-live-chat-text-input-field-renderer-0:hover #unfocused.yt-live-chat-text-input-field-renderer {
        background-color: #666;
    }
    .yt-live-chat-text-input-field-renderer-0 #focused.yt-live-chat-text-input-field-renderer {
        background-color: #777;
    }
    .yt-emoji-picker-category-renderer-0 yt-formatted-string.yt-emoji-picker-category-renderer {
        background-color: #333;
        color: #AAA;
    }
    .paper-input-container-0 .input-content.paper-input-container label,
    .paper-input-container-0 .input-content.paper-input-container .paper-input-label {
        color: #AAA;
    }
    .paper-input-container-0 .input-content.paper-input-container label,
    .paper-input-container-0 .input-content.paper-input-container .paper-input-label {
        color: #AAA;
    }
    .paper-input-container-0 .input-content.paper-input-container input,
    .paper-input-container-0 .input-content.paper-input-container textarea,
    .paper-input-container-0 .input-content.paper-input-container iron-autogrow-textarea,
    .paper-input-container-0 .input-content.paper-input-container .paper-input-input {
        color: #AAA;
    }
    .yt-emoji-picker-renderer-0 #search-panel.yt-emoji-picker-renderer {
        background-color: #555;
        color: #888;
    }
    .yt-live-chat-text-message-renderer-0 #menu.yt-live-chat-text-message-renderer {
        background: linear-gradient(to right, transparent, #555)
    }
    .paper-menu-0 {
        background-color: #282828;
    }
    .ytd-menu-navigation-item-renderer-0 yt-formatted-string.ytd-menu-navigation-item-renderer {
        color: #666;
    }
    /* < Live chat */

    /* Search page > */
    .search-header {
        border-color: #555;
    }
    .yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a {
        color: #777;
    }
    .filter-col-title {
        color: #AAA;
    }
    .filter {
        color: #666;
    }
    .filter.filter-disabled {
        color: #333;
    }
    .filter.filter-selected {
        color: #AAA;
    }
    #results .search-message, #results .safety-mode-message, #results .video-alt-query {
        color: #777;
    }
    #results .section-list li .item-section .branded-page-box {
        border-color: #555;
    }
    /* < Search page */

    /* Content page > */
    .yt-default h1,
    .yt-default h2,
    .yt-default h3,
    .yt-default h4,
    .yt-default h5,
    .yt-default h6,
    h1.yt,
    h2.yt,
    h3.yt,
    h4.yt,
    h5.yt,
    h6.yt {
        color: #888;
    }
    #view-as-notification {
        background-color: #3D4E6B;
    }
    #c4-header-bg-container {
        border-color: #555;
    }
    .channel-header .secondary-header-contents {
        background-color: #222;
        border-color: #555;
    }
    .secondary-header-contents .nav-text {
        color: #AAA;
    }
    .site-center-aligned #footer-container {
        background-color: #111;
    }
    .site-center-aligned .yt-card {
        background-color: #181818;
    }
    .yt-card.yt-uix-flipwidget-back-side {
        background: none;
    }
    .exp-css-ellipsis .yt-ui-ellipsis {
        background-color: #181818;
    }
    .exp-css-ellipsis .yt-uix-expander-ellipsis {
        background-color: #181818;
    }
    .yt-uix-expander-ellipsis {
        background-color: #181818;
    }
    .yt-badge {
        border-color: #555;
        color: #888;
    }
    .yt-badge-live {
        border-color: #BF1813;
        color: #BF1813;
    }
    .yt-thumb {
        background-color: #222;
    }
    .branded-page-v2-primary-col .branded-page-box {
        border-color: #555;
    }
    .c4-empty-module.branded-page-box.business-email-inline {
        border-bottom: none;
    }
    .section-list li .item-section .branded-page-box {
        border-color: #555;
    }
    .branded-page-v2-primary-col .branded-page-box.video-player-view-component:last-child,
    .branded-page-v2-primary-col .branded-page-box.c4-featured-content:last-child,
    .branded-page-v2-primary-col .branded-page-box.c4-featured-content-editor:last-child,
    .branded-page-v2-primary-col .branded-page-box.welcome:last-child {
        border-color: #555;
    }
    .branded-page-v2-primary-col .yt-uix-inlineedit-view-container .branded-page-box {
        border-color: #555;
    }
    .branded-page-v2-primary-col .about-description {
        color: #777;
    }
    .about-description-editor .c4-module-is-editable:hover,
    .about-business-email-editor .c4-module-is-editable:hover,
    .about-metadata-editor .c4-module-is-editable:hover {
        background-color: #222;
    }
    .about-metadata .yt-uix-button-c4-view-action {
        border-top-color: #555;
    }
    .about-metadata-label-border-top {
        border-top-color: #555;
    }
    .channel-header .branded-page-header-title .branded-page-header-title-link {
