css:
@-moz-document domain("www.pandora.com") {
#playerBar { background-image: url('http://i.imgur.com/xiqQHH3.png'); }

#brandingBar { background: #101010; }

#brandingBar .middlecolumn , #brandingBar .rightcolumn , #brandingBar .rightcolumn a { color: #ffffff ; }

#brandingBar .rightcolumn .user_activator:hover { background: rgba(255,255,255,0.1) ;}

#playerBar .progress .progressBar, #playerBar .volumeBar { background: white; }

.contentnav , .user_menu, #mainContentContainer .top, #brandingBar .rightcolumn  { background: #101010; }

.stationContent {background: #ccf;}

#promobox {display: none; }

.slidesBackground {width: 1500px; background: #040c2a;}

.shuffleStationLabelCurrent, .shuffleStationLabelCurrent a { color: black; } 

.stationListItem.selected {background: #ccf; border-top: 0 px ; border-bottom: 0 px; }

.stationListItem .options { background: white;}

#maincontentcontainer .home {background: black;}

.contentContainer {background: #ccf;}

#playerBar .thumbUpButton.indicator a:link, #playerBar .thumbUpButton.indicator a:visited, #playerBar .thumbUpButton.indicator a:hover, #playerBar .thumbUpButton.indicator a:active {background: url('http://oi62.tinypic.com/1hvv2o.jpg');}

#playerBar .thumbDownButton.indicator a:link, #playerBar .thumbDownButton.indicator a:visited, #playerBar .thumbDownButton.indicator a:hover, #playerBar .thumbDownButton.indicator a:active {background: url('http://oi57.tinypic.com/1z49v11.jpg');}

#ad_container, #accountMessageRibbon, #footer, .track_detail_close, .divider.last,
#videoPageInfo, .stationChangeSelectorNoMenu, .logo, .upgradeLink, .user_menu + SPAN, 
#upgradeOffer {
display:none !important;
height:0px !important;}

#brandingBar .middlecolumn {
display:none !Important;}

#main #mainContentContainer {
margin-top:34px !Important;}

.top {
position:fixed !important;
top:65px !important;
width:100% !important;
z-index:9999 !important;}

.stationContent {
z-index:1 !important;}

.contentnav {
position:fixed !important;
top:70px !important;
left:0px !important;
width:100% !Important;
z-index:2 !important;}

.artist_detail .content_right {
width:280% !important;}

.tip_header, .tip_box {
border-radius:3px !important;}

.tip_header {
position:relative !important;
top:-1px !Important;
border-bottom-left-radius:0px !important;
border-bottom-right-radius:0px !important;}

.tip_header {
margin-top:-1px !Important;}

#brandingBar {
background-image:none !important;
background-color:#3a4a6a !important;
color:#FFF !important;
position:absolute !important;
right:0px !important;
height:25px !important;
top:70px !important;
z-index:2 !important;
min-width:200px !important;
max-width:200px !Important;}

.user_activator {
background:none !important;
border:0px !important;}

.homeRightSide {
background-color:#d9e0ea !important;}

#adLayout {
width: 665px !important;}

#topnav { 
top:-1px !important;
max-height:10px !important;
min-height:10px !important;}

.adSupported-layout #adLayout, .adSupported-layout .contentContainer,
div#mainContentContainer.contentContainer div.content div#mainContent div.home div.homeRightSide div.slidesBackground div img {
width: 100% !important;}

.contentContainer .top, .view-2, .home .homeRightSide, .stationContent, #mainContent, .home.fxCol-cont-1.view-2, 
#mainContentContainer {
border-radius:0px !important;}

#mainContainer {
height:100% !important;}

.home .homeRightSide {
background-color: #042955;}

.panel-top.clearfix {
position:absolute !important;
right:28% !important;}

* {
font-family:Calibri !important;}

#main {
top:65px !important;}
}
