css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* Awesome bar */
.urlbar-icon#star-button {
  list-style-image: url(data:image/png;base64,);
}

.urlbar-icon#star-button:hover {
  list-style-image: url(data:image/png;base64,)
}

.urlbar-icon#star-button[starred] {
  list-style-image: url(data:image/png;base64,);
}

.urlbar-icon#star-button[starred]:hover {
  list-style-image: url(data:image/png;base64,);
}

.urlbar-icon#star-button:active {
  list-style-image: url(data:image/png;base64,) !important;
}

#star-button-animatable-box {
  display: none;
}

/* bookmarking panel */
#editBookmarkPanelStarIcon {
  list-style-image: url(data:image/png;base64,);
  width: 48px;
  height: 48px;
}

#editBookmarkPanelStarIcon[unstarred] {
  list-style-image: url(data:image/png;base64,);
}

/* URL bar autocomplete */

.ac-result-type-bookmark,
.autocomplete-treebody::-moz-tree-image(bookmark, treecolAutoCompleteImage) {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: rect(0px 16px 16px 0px);
  width: 16px; 
  height: 16px;
}

/* Unsorted Bookmarks icon */
/* As of now, this still has a yellow star, but as there are plans afoot to blueify it as well, we already handle it */

#BMB_unsortedBookmarks,
#panelMenu_unsortedBookmarks {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: auto;
}

#editBMPanel_folderMenuList[selectedIndex="2"],
#editBMPanel_unfiledRootItem {
  list-style-image: url(data:image/png;base64,) !important;
  -moz-image-region: auto !important;
}

treechildren::-moz-tree-image(container, OrganizerQuery_UnfiledBookmarks) {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: auto;
}

/* organize, view and maintenance buttons icons */
/* ditto */

#organizeButton, 
#viewMenu, 
#maintenanceButton {
  list-style-image: url(data:image/png;base64,);
}
