css:
/* 
Facebook Nisekoi Theme by Inorinne
*/

@-webkit-document domain("facebook.com")
{
body
{
  cursor: url('data:image/x-icon;base64,'), auto; 
}

body.home, body.hasLeftCol
{
  background:url("http://i279.photobucket.com/albums/kk157/chenyiren/CSS%20NISEKOI/nisekoi_wallpaper_by_redeye27-d7anzh6-1.jpg") center fixed;
  background-size:cover;
  
}



#contentCol
{
  background:transparent !important;
}


div._4-u2, div.mtl, ._5i-7
{
  background:rgba(255, 255, 2550, 0.8) !important;
}
        
div.fixed_elem.stuck, ._4-u2
{
  background:rgba(255,255,255,1) !important;
}


div#pagelet_ego_pane, div#pagelet_rhc_footer.pagelet
{
  display:none !important;
}
    
    
.UFIList, .UFIRow, .UFIComment
{
  background:none !important;
}

span.fwb
{
  color:red !important;
}

p
{
  color:black !important;
}


div._1dsp
{
  opacity:0.5 !important;
}
  
/*Border*/

div._6m2, a._4-eo, img.fbphotoimage, div#imagestage, div.photoUnit a img,a.profilePicChangePhotoLink
{
  border:4px solid rgba(254, 254, 232, 0.8);
  border-radius:8px;
}
  

/*Blue Bar*/

h1#pageLogo._5bfh a
{
  background:url("http://2.bp.blogspot.com/-7mbhZNwym5E/UvlQuM2UuMI/AAAAAAAABgA/gWLJR0pZ7iM/s1600/nisekoi.png") center no-repeat !important;
  position:absolute !important;
  width:100px;
  left:-32px;  
}
  

div.aboveSidebar, div#pageHead
{
  background:rgb(35,176,232) !important;
}
  

div#headNav div.rfloat ul#pageNav
{
  position:fixed !important;
  left:820px;
}


div._585-
{
  position:fixed !important;
  left:170px;
  width:600px;
}

  
/*Left Column*/

div#leftCol
{
  background:linear-gradient(to bottom, rgba(252,238,189,0.8), rgba(162,134,85,0.6));
}
  

div.linkWrap.noCount, div.linkWrap.hasCount
{
  color:rgba(101,82,46,1);
}


div.linkWrap.noCount:hover, div.linkWrap.hasCount:hover, ul#uiSideNav li.sideNavItem
{
  color:red;
}
    

h4.navHeader a div.clearfix
{
  border-style:solid;
  border-color:rgba(162,134,85,1);
  border-radius:6px;
  border-width:2px;
  background:rgba(252,238,189,0.8);
  left:30px;
  position:inherit;
}


div#pagesNav.homeSideNav h4.navHeader
{
  border-style:solid;
  border-color:rgba(162,134,85,1);
  border-radius:6px;
  border-width:2px;
  background:rgba(252,238,189,0.8);
}

/*Like Comment Share button*/

a.share_action_link, a[id^="js"], a.UFILikeLink, input.uiLinkButtonInput, a[data-hovercard*="hovercard"]
{
  color:blue !important;
}
}
@-webkit-document url("http://www.facebook.com/"), url("https://www.facebook.com/"), url-prefix("https://www.facebook.com/?stype=lo"), url-prefix("http://www.facebook.com/?stype=lo")
{
body.fbIndex, body.UIPage_LoggedOut
{
  background:url("http://i279.photobucket.com/albums/kk157/chenyiren/CSS%20NISEKOI/fff-nisekoi-01-d80b14c1-mkv_snapshot_05-41_2014-01-14_21-19-30.jpg") fixed no-repeat;
  background-size:cover !important;
  overflow:hidden !important;
  height:100%;

}


a
{
  visibility:hidden !important;
  
}


#globalContainer, #pagelet_bluebar
{
  visibility:hidden;

}


div.menu_login_container.rfloat._ohf
{
  position:fixed !important;
  left:354px;
  top:200px;
  visibility:visible !important;
  box-shadow: -22px /*right*/ -10px /*top*/ 79px /*spread*/ -1px  #CCCC00;
  border:yellow 6px solid;
  background:rgba(255, 255, 152, 0.6);
  width:400px;
  height:120px;
  padding:77px 115px 11px 113px;
  border-radius:40px;
  
}


label[for=email], label[for=pass]
{
  color:red !important;

}


/*Facebook Logo*/
a.lfloat
{
  visibility:visible !important;
  position:fixed !important;
  left:561px;
  top:110px;
  background:rgba(255, 255, 95, 0.6);
  border-radius:40px;
  padding:2px 20px 10px 20px;

}

img.phoneImage
{
  display:none !important;
}
}
