css:
@-moz-document domain(d3.ru) {

/* конские посты */

.b-article_text { 
    font: 1em Verdana, sans-serif !important;
    line-height: 1.5 !important;
    margin-bottom: 10px !important;
}
.b-image_description { 
    font: italic 1em Verdana, sans-serif !important;
    min-height: 0 !important;
}
.b-title {
    font: 2.41em Verdana, sans-serif !important;
    min-height: 0 !important;
}
.b-post_subtitle { 
    font: 1.26em Verdana, sans-serif !important;
    line-height: 1.41 !important;
    min-height: 19px !important;
}
.b-post_header_image { 
    padding-bottom: 35px !important;
}
.b-post_header_content_wrapper { 
    margin-top: 0 !important;
    height: auto !important;
}
.b-post_article_content { 
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    margin-bottom: 21px !important;
    margin-top: 28px !important;
}
.b-post_article { 
    margin-bottom: 0 !important;    
    padding-bottom: 0 !important;
}

.b-article_text blockquote, blockquote { 
    font: italic 1.37em Georgia, "Times New Roman", serif !important;
    line-height: 1.41 !important;
}

/* голосование в конских постах */

.b-post_header .vote_result,
.b-vote_container,
.b-vote_container_bg .b-svg-icon,
.b-vote_container_bg .b-svg-icon svg {
    width: 38px !important;
}
.b-vote_container {
    left: -60px !important;
    width: 60px !important;
    height: 68px !important;
    top: 23px !important;
}
.b-vote_container_bg .b-svg-icon, .b-vote_container_bg .b-svg-icon svg {
    height: 38px !important;
    margin-top: 6px !important;    
    margin-left: 0 !important;
}
.b-post_header .vote_result {
    font: bold 12px Verdana, sans-serif !important;
    height: 14px !important;
    top: 29px !important;
    left: -1px !important;
}
.b-post_header .vote_button {
    border: #ccc solid 1px !important;
    left: 8px !important;
}
.b-post_header .vote_button_plus {
    top: 0 !important;
}
.b-post_header .vote_button_minus {
    bottom: -6px !important;
}
.b-post_header .vote {
    height: 68px !important;
    margin-top: -7px !important;
    width: 60px !important;
}
/* стиль всех тегов и оформления комментариев */

.b-post_tags {
    font-size: 0.85em !important;
}
.b-post_tags .tag {
    background-color: #E6E6E6 !important;
    border-right-color: #D1D0D0 !important;
    border-bottom-color: #D1D0D0 !important;
    padding: 2px 3px !important;
}

.b-social_network_icons { 
    float: right !important;
    margin-top: 0 !important;
    right: 0 !important;    
    left: auto !important;
    width: 234px !important;
    top: -28px !important;
}

.l-content_main .b-social_network_icons { 
    top: 1px !important;
}
.b-post_article_comments { 
    padding-left: 165px !important;
}
.l-subdomain_post_custom_design {     
    margin-bottom: 0 !important;
}

/* красное пятно */
.b-header_nav a.b-fui_icon_button .b-header_nav_count u {
    background-color: transparent !important;
    color: #888 !important;
    font-weight: normal !important;
    vertical-align: baseline !important;
    font-size: 11px !important;
    top: 0 !important;
}

}
