css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ign.com") {
	.listElmnt-iconsComments{display:none!important;}
	@media screen and (max-width:1560px) {
		body{ font-size:16px!important; }
	}
}
@-moz-document url-prefix("http://www.ign.com/videos/") {
	body{border-left:0!important;}
	.layout-ms .column-main.column-main.column-main-video {
		height: 551px !important;
		width: 980px!important;
	}
	#IGNPlayer {
		height: 551px !important;
		width: 980px!important;
	}
	#header-container.spine:after {background:#292727!important;}
	#header-container .browse {background:transparent!important;}
	#header-nav .popular-links a,
	#spine-container.horizontal .title {color:#fff !important;}
	#header-nav.fixed .popular-links a {color:#000!important;}
	#header-container .popular-links a:hover {color:#d3222a!important;}
	#spine-container.horizontal .trending .title:after {background-color:rgba(0, 0, 0, 0.12)!important;}
	
	#header-container #ign-logo,
	#header-container #browse-button {background-color:#57403A!important;}
	#header-container #ign-logo:after {background-color:transparent!important;}
	#header-container #ign-logo:hover {background-color:#5A4843!important;}
}
