css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(softonic.com),
domain(en.softonic.com),
domain(softonic.fr),
domain(softonic.de),
domain(softonic.it),
domain(softonic.com.br),
domain(softonic.pl),
domain(softonic.jp),
domain(nl.softonic.com),
domain(tr.softonic.com),
domain(ko.softonic.com),
domain(sv.softonic.com)

{
    
body {
    background: #e6e6e6}
    
div { background: #e6e6e6 !important;}
    
.banner-center 
      {display: none !important}
    
.corporative-header .section-applications 
    { background: #0B0B3B !important;}
    
#master-1    
     {display: none !important}
    
DIV#ads-container-top.csa-ads-container.csa-ads-top
     {display: none !important}
    
IFRAME#slave-1-1    
    {display: none !important}
    
DIV#ads-container-bottom.csa-ads-container.csa-ads-bottom    
     {display: none !important}
       
html   
    { background: #0B0B3B !important;}
    
.list-program .row-pair    
    { background: #BCA9F5 !important;}
    
 
.list-program .row-pair .basic-data h5, .list-program .extra-info h5 ,.list-program .row-pair .basic-data dl, .list-program .extra-info dl 
 { background: #BCA9F5 !important;}
  
.list-program .basic-data h5 span 
    {color: #000000;}
  
.license2, .license3, .license8 {
    color: #8A0808;}    
      
.corporative-header .sections-bar
    { color: #0B0B3B;
        background: #0B0B3B !important;}
.list li {
    overflow: show;
    padding: 5px 15px;
    border-bottom: 3px dotted #5D1313;
}        
    
    
}
