css:
@-moz-document domain('wykop.pl') {
	/* opis danego wykopu */
	.article > div p.text {
		font-size: 12px;
	}

	/* ogólny box z tekstem itp. danego wykopu */
	.comments-stream .article.preview > div:last-child {
		margin-left: 0px;
	}

	/* miniaturki */
	.article.preview .media-content {
		float: right;
	}

	.article.preview .media-content img {
		width: 150px;
		border: 1px solid #d8d8d8;
		transition: 0.5s;
		-webkit-transition: 0.5s;
		-moz-transition: 0.5s;
	}

	.article.preview .media-content img:hover {
		border: 1px solid #4383af;
	}

	.w-item.mini span.image {
		width: 60px;
		height: 33.75px;
	}

	.w-item.mini span.image img {
		width: 100%;
		height: 100%;
	}
	
	.w-item.mini span.image + span {
		margin-left: 0;
		padding-bottom: unset;
	}
	
	.w-item.mini {
		min-height: 0;
	}

	.w-item .results {
		position: unset;
		left: unset;
		bottom: unset;
	}

	.w-item.mini + .recommended-by {
		margin-top: 0;
	}
	
	img:not([src]) {
		visibility: hidden;
	}
}
