css:
@-webkit-document domain("facebook.com") {
body.UIPage_LoggedOut, body.hasSmurfbarLoggedOut
{
  background:url("http://i279.photobucket.com/albums/kk157/chenyiren/CSS%20madoka/MahouShoujoMadokaE29886Magicafull1716578.jpg") no-repeat  !important;
  background-size:cover !important;
  overflow:hidden;
  
}


body.UIPage_LoggedOut div#globalContainer, body.hasSmurfbarLoggedOut div#globalContainer
{
  display:none !important;
}

/*Blue Bar*/
body.UIPage_LoggedOut div#pagelet_bluebar, body.hasSmurfbarLoggedOut div#pagelet_bluebar
{
  visibility:hidden !important;
}

/*Facebook Logo*/

body.UIPage_LoggedOut a.lfloat
{
  visibility:visible !important;
  position:fixed !important;
  margin:10px 0 0 -82px;
  top:3%;
  left:51.2%;
  width:0%;
  height:0%;
  min-width:0%;
  min-height:0%;
}

/*Email & password*/
body.UIPage_LoggedOut table
{
  visibility:visible !important;
  position:fixed !important;
  margin:90px 0px 0px -158px;
  position: fixed !important;
  top:2%;
  left:51%;
  width:29%;
  height:12%;
  min-width:0%;
  min-height:0%;

}

/*Border*/
body.UIPage_LoggedOut div.loggedout_menubar
{
  visibility: visible !important;
  position: fixed !important;
  border: solid;
  border-color:rgba(31,30,41,0.8);
  border-radius: 15px;
  border-width: 3px;
  left: 29%;
  top: 2.4%;
  height: 24%;
  width: 47%;
  min-width: 0%;
  min-height: 0%;
  background:rgba(57,60,91,0.5) !important;
  box-shadow: 0px 0px 14px 6px rgba(128,167,200,0.8);

}


body.UIPage_LoggedOut label.uiInputLabelLabel, a[rel="nofollow"]
{
  color:rgb(204, 204, 254) !important;
}
}


@-webkit-document domain("facebook.com") {
body.home, body.hasSmurfbar
{
  background:url("http://i279.photobucket.com/albums/kk157/chenyiren/CSS%20madoka/AkemiHomurafull1715448.jpg") no-repeat center fixed !important;
  background-size:cover !important;
}

div.aboveSidebar
{
  background:rgb(87, 87, 87) !important;
}

div#pagelet_ego_pane
{
  display:none !important;
}


div#pagelet_rhc_footer
{
  background-color:rgba(255, 255, 255,0.8) !important;
  border-radius:3px;
  color:white;
}


div#pagelet_rhc_footer a, div.rhcFooterCopyright span
{
  color:rgba(0, 0, 0, 0.7);
}



/*News Feed*/
div#contentCol
{
  background:transparent !important;
}

/*Left Column*/
div#leftCol
{
  background:linear-gradient(to bottom, rgba(140,138,139,0.7), rgba(100,100,101,0.5)) !important;
}

div#leftCol a, div#leftCol span
{
  color:rgba(255,255,255,0.9) !important;
}


div._4-u2
{
  background:rgba(255, 255, 255,0.6) !important;
}
/*END News Feed*/

  
/*Home Comment*/

/*Border*/
div.uiUfi
{
  background:green !important;
  margin-top:-2px;
}

/*Comment INNER*/
li.UFIComment
{
  background:transparent !important;
}

/*Comment TEXT*/

ul.UFIList span.UFICommentBody span
{
  color:white !important;
}

/*Likes*/
div.uiUfi
{
  background:transparent !important;
}

/*View Comments, View Shares*/
div.uiUfi li.UFIPagerRow, ul.UFIList li.UFIShareRow
{
  background:transparent !important;
}

/*Add Comment*/
div.uiUfi li.UFIAddComment
{
  background:transparent !important;
}
/*END Home Comment*/



/*Profile*/
li.fbTimelineUnit div.timelineUnitContainer, li.fbTimelineTwoColumn div.timelineUnitContainer
{
  background:rgba(86,86,86,0.5) !important;
  border:none !important;
}

/*Top Border*/
li.fbTimelineUnit div.topBorder, li.fbTimelineTwoColumn div.topBorder
{
  background:transparent !important;
}

/*Border Bottom*/
li.fbTimelineUnit div.bottomBorder, li.fbTimelineTwoColumn div.bottomBorder
{
  background:transparent !important;
}

/*Background Button LIKE STOP NOTFICATION etc..*/
div.fbTimelineFeedbackActions
{
  background:rgba(68, 68, 68, 0.69) !important;
  margin:-3px 0px 0px 0px !important;
  border:none !important;

}

/*Border Comments*/
form.commentable_item ul.UFIList
{
  background:rgba(46,46,46,0.5) !important;
}

/*Likes*/
ul.UFIList li.UFILikeSentence
{
  background:transparent !important;
}

/*About*/
div.timelineReportContent div.timelineUnitContainer
{
  background:inherit !important;
}

/*Photo Border*/
div.letterboxedImage.photoWrap.verticallyCentered
{
  background:transparent !important;
}
/*END PROFILE*/

ul.UFIList a
{
  color:rgba(203, 183, 255,1) !important;
}

ul.UFIList li.UFIUnseenItem:after
{
  background-color:red !important;
}

/*Like Button*/
span a.UFILikeLink, input.uiLinkButtonInput, a.share_action_link, span.share_action_link, a.UFIBlingBox
{
  color:white !important;
}
}
