css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {
html {
    font-size: 60% !important;
}
div.article.clearfix.preview.dC {display: block !important;}
div.article.clearfix.preview {display: none !important;}

#dyingLinksBox.comments-stream .article.preview .diggbox {display: none !important;}
#dyingLinksBox.comments-stream .article.preview > div > div {display: none !important;}
li.link.dying-links.mark-bg.iC {display: none !important;}


.rbl-block {
    border-bottom-right-radius: 0px !important;
    border-bottom-left-radius: 0px !important;
    border-bottom: 0px solid #DAE2E4 !important;
    border-left: 0px solid #E5EAEC !important;
    border-right: 0px solid #E5EAEC !important;
    margin-bottom: 3px !important;
}

.r-block, .grid-right > .r-block, .grid-right > div {
    margin-bottom: 1px !important;
}

#w-button {
display : none;
	}
.author p b span {
    font-size: 1.2rem !important;
}
.comments-stream .article {
    margin-top: -4px !important;
}
.media-content.m-reset-float{
	float: right !important;
	}
.article.preview .media-content + div { 
	margin-left: 0px !important;
	}
.grid-main {
    margin-right: 320px !important;
}
    
.article.preview .media-content img {
width: 150px;
}

/* button.submit, .button.submit, input[type="submit"] {
background-color: #323232 !important;}  */ /* usun dla innego koloru przycisku niz niebieski */
    
    
    
    
    
}
@-moz-document url("http://www.wykop.pl/") {
Li.entry.iC{
    display: none;
}
}


@-moz-document url-prefix("http://www.wykop.pl/dodatki/"){
div.article.clearfix.preview.dC {display: block !important;}
div.article.clearfix.preview {display: block !important;}
}

@-moz-document url-prefix("http://www.wykop.pl/mikroblog/"){

.row.elements ul, .row.elements ul li {
    float:right !important;
    position: absolute !important;
    display: none !important;
}
.lcontrast .hide {
    float: right !important;
    position: absolute !important;
    right: 0px !important;
    bottom: 0px !important;

}
