css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("proxer.me") {
    
html body{
background-color: rgb(10,10,10)!important;
background-image: url(http://i.imgur.com/OOTDfqo.png)!important;
color: #aaa!important;
}
    
.navlogo {
background-image: url("http://i.imgur.com/EpSHPhZ.png");
}
    
.colorbox.gray {
background-color: #272727;
}
    
#nav {
background-color: #272727 !important;
border-bottom: 1px solid #191919 !important;    
} 
    
#main {
background-color: #272727 !important;
border: 1px solid #191919 !important;
}
    
.topmenu ul {
background-color: #272727 !important;
border-color: -moz-use-text-color #191919 #191919 !important;
border-right: 1px solid #191919 !important;
}

#notificationBubble, #loginBubble, .customBubble {
    background: none repeat scroll 0 0 #272727;
    border: 1px solid #ffffff !important;;
}
#notificationBubble:after, #loginBubble:after, .customBubble:after {
    border-color: #ffffff rgba(0, 0, 0, 0) !important;
}
          
ul#simple-navi, ul#simple-navi-wiki {
background-color: #3c3c3c;
}

/*Link Leiste*/    
   
ul#simple-navi li a, ul#simple-navi-wiki li.active a, ul#simple-navi-wiki li a:hover {
background-color: #ffffff !important;
color: #000000 !important;
background:none;
}
ul#simple-navi-wiki li a, ul#simple-navi li.active a, ul#simple-navi li a:hover {
background-color: #272727 !important;
color: #FFFFFF !important;
background:none;
}
    
    
    #box-table-a th {
    background: none repeat scroll 0 0 #3c3c3c;
    border-bottom: 1px solid #FFFFFF;
    color: #FFFFFF;
}
#box-table-a td {
    background: none repeat scroll 0 0 #272727;
    border-bottom: 1px solid #FFFFFF;
    color: #FFFFFF;
}
#box-table-a tr:hover td {
    background: none repeat scroll 0 0 #FFFFFF;
    color: #777777;
}
#box-table-a tr:hover a {
    color: #000000;
}
    
#chat {
background-color: #272727;
border-color: #191919;
}
    
.details td {
background-color: #3c3c3c;
border: 0px;
}
    
}
