css:
@-moz-document regexp(".*wikipedia.org/(wiki|w)/((?!Main_Page).)*$") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Light'), local('Roboto-Light'), url(https://themes.googleusercontent.com/static/fonts/roboto/v10/d-QWLnp4didxos_6urzFtg.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Regular'), local('Roboto-Regular'), url(https://themes.googleusercontent.com/static/fonts/roboto/v10/grlryt2bdKIyfMSOhzd1eA.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 700;
  src: local('Roboto Bold'), local('Roboto-Bold'), url(https://themes.googleusercontent.com/static/fonts/roboto/v10/vxNK-E6B13CyehuDCmvQvw.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 300;
  src: local('Roboto Light Italic'), local('Roboto-LightItalic'), url(https://themes.googleusercontent.com/static/fonts/roboto/v10/iE8HhaRzdhPxC93dOdA05z8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 400;
  src: local('Roboto Italic'), local('Roboto-Italic'), url(https://themes.googleusercontent.com/static/fonts/roboto/v10/biUEjW7P-lfzIZFXrcy-wQ.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 700;
  src: local('Roboto Bold Italic'), local('Roboto-BoldItalic'), url(https://themes.googleusercontent.com/static/fonts/roboto/v10/owYYXKukxFDFjr0ZO8NXhz8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Source Code Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Source Code Pro'), local('SourceCodePro-Regular'), url(https://themes.googleusercontent.com/static/fonts/sourcecodepro/v4/mrl8jkM18OlOQN8JLgasDxBHWFfxJXS04xYOz0jw624.woff) format('woff');
}

    body{
        font-family:'Roboto' !important;
        background:#fff !important;
    }
    h1{
        border:0px !important;
        font-family:'Roboto' !important;
        font-weight:700 !important;
        font-size:36px !important;
    }
    h2{
        border:0px !important;
        font-family:'Arial' !important;
        font-weight:400 !important;
        font-size:26px !important;
    }
    h3{
        border:0px !important;
        font-family:'Arial' !important;
        font-weight:400 !important;
        font-size:16px !important;
    }
    #content h3{
        font-size:20px !important;
    }
    p{
        font-weight:300 !important;
        line-height:1.9 !important;
    }
    table,td,th{
        border:0px !important;
        border-collapse:collapse !important;
        padding:5px !important;
    }
    ul,li{
        background:transparent !important;
        border:0px !important;
        line-height:1.8 !important;
        font-size:14px !important;
        font-weight:300 !important;
    }
    li span{
        background:transparent !important;
        border:0px !important;
        font-size:14px !important;
    }
    .reference,hr{
        /*display:none;*/
  		font-size:9px !important;
    }
    dl{
        font-weight:300 !important;
    }
    dt{
        font-style:italic;
        font-size:16px !important;
        margin-top:10px !important;
    }
    dd{
        font-size:15px !important;
    }
    #p-logo a{
        background-image:url('http://icons.iconarchive.com/icons/sykonist/popular-sites/128/Wikipedia-icon.png') !important;
    }
    code{
        padding:3px !important;
        background:transparent !important;
        color:#39892f !important;
    }
    tt{
        font-family:'Source Code Pro','Lucida Sans Unicode','Courier New' !important;
        font-size:16px !important;
    }
    a,a:link,a:hover,a:visited{
        color:#067BAD !important;
    }
    .new,.new:link,.new:visited{
        color:#cd5b45 !important;
    }
    blockquote{
        border:0px !important;
        font-style:italic !important;
        font-size:20px !important;
        margin:35px 75px !important;
        line-height:1.5 !important;
        font-weight:300 !important;
        background:transparent !important;
    }
    select{
        padding:0px 10px;
    }
    .mw-ui-button,.mw-ui-progressive{
        border-radius:4px !important;
        border:1px solid #347Bff !important;
        color:#347Bff !important;
        background:#fff !important;
        text-shadow:0px 0px 0px;
    }
    .mw-ui-button.mw-ui-progressive:hover,.mw-ui-button.mw-ui-progressive:focus,.mw-ui-button.mw-ui-progressive:active,.mw-ui-button:hover,.mw-ui-progressive:hover,.mw-ui-button:focus,.mw-ui-progressive:focus{
        box-shadow:0px 0px 0px !important;
        text-shadow:0px 0px 0px !important;
        border:1px solid #347Bff !important;
    }
    #wpSave,#wpPreview,#wpDiff{
        border-radius:4px !important;
        border:1px solid #347Bff !important;
        color:#347Bff !important;
        background:#fff !important;
        text-shadow:0px 0px 0px;
        padding: 0.5em 1em;
        cursor:pointer;
    }
    #wpSave:hover,#wpPreview:hover,#wpDiff:hover{
        color:rgba(52,123,255,0.5) !important;;
    }
    
    /* Search */
    #simpleSearch{
        border:1px solid rgba(0,0,0,0.25) !important;
        background:transparent !important;
        color:#000 !important;
        border-radius:5px !important;
        padding:7px 7px 0px 7px !important;
    }
    #searchInput{
        border:0px !important;
        background:transparent !important;
        font-family:'Roboto' !important;
        font-size:16px !important;
        padding:4px 7px 7px 7px !important;
    }
    #searchButton{
        right:7px !important;
    }
    
    .mw-ui-input{
        font-family:'Roboto' !important;
        border:1px solid #347Bff !important;
        color:#000 !important;
        background:#fff !important;
        font-size:16px !important;
    }
    .mw-ui-input:focus{
        box-shadow:0px 0px 0px !important;
    }
    .suggestions{
        font-weight:300 !important;
        font-size:15px !important;
        max-width:300px !important;
        margin-top:15px !important;
        border:0px !important;
        box-shadow:2px 2px 10px 2px rgba(0,0,0,0.5);
    }
    .suggestions-results{
        border:0px !important;
    }
    .suggestions-result{
        padding:10px !important;
    }
    .imeselector,#searchEngines{
        display:none !important;
    }
    .mw-search-formheader{
        border:0px !important;
        background:transparent !important;
    }
    .mw-search-result-heading,.searchresult,.searchmatch{
        font-size:14px !important;
        font-weight:400 !important;
        padding:3px 0px !important;
    }
    .searchmatch{
        font-weight:700 !important;
    }
    .mw-search-result-data,.mw-search-createlink{
        display:none;
    }
    
    /* Основная страница */
    #mw-page-base,.vectorTabs{
        background:transparent !important;
        border:0px !important;
    }
    #content{
        border:0px !important;
        margin-left:215px !important;
    }
    .portal{
         background:transparent !important; 
    }
    #mw-panel{
        width:200px !important;
        background:rgba(0,0,0,0.04);
    }
    #mw-head{
        opacity:0.5;
        border:0px !important;
        width: calc(100% - 233px) !important;
        -webkit-transition-property:opacity !important;
        -moz-transition-property:opacity !important;
        -o-transition-property:opacity !important;
        -ms-transition-property:opacity !important;
        transition-property:opacity !important;
        -webkit-transition-duration:0.5s !important;
        -moz-transition-duration:0.5s !important;
        -o-transition-duration:0.5s !important;
        -ms-transition-duration:0.5s !important;
        transition-duration:0.25s !important;
    }
    #mw-head:hover{
        opacity:1 !important;
    }
    
    
    .uls-settings-trigger{
        display:none !important;
    }
    
    #left-navigation {
        margin-left:5px !important;
    } 
    
    .wikitable{
        font-weight:300 !important;
    }
    .wikitable td,.wikitable th{
        border:1px dashed rgba(0,0,0,0.07) !important;
    }

    pre{
        line-height:1.3 !important;
        font-family:'Source Code Pro','Lucida Sans Unicode','Couriew New','Arial' !important;
        border:0px !important;
        background:0px !important;
        font-size:16px !important;
    }
    .mw-code{
        border:0px !important;
        padding:17px !important;
        background:0px !important;
        font-size:16px !important;
    }

    .thumbinner,.thumb,.toccolours,.graytable{
        background:transparent !important;
        border:0px !important;
        font-size:16px !important;
    }
    .thumbimage{
        border:0px !important;
    }
    .thumbcaption{
        font-style:italic !important;
        margin:7px 0px !important;
    }
    .thumbimage:hover{
        opacity:0.85 !important;
    }
    
    /* Курсивный небольшой жирноватый шрифт в начале страницы */
    .dablink{
        margin:14px 0px !important;
        font-weight:300 !important;
        font-size:18px !important;
        
        
        
    }
    
    /* Надпись - материал из вики и текущая страница не проверялась*/
    #siteSub,.flaggedrevs_preview{
        display:none !important;
    }
    
    /* Различные таблички перед страницей */
    .ambox,.tmbox,.Note,.informationbox{
        margin:5px 0px !important;
        font-size:18px !important;
        font-weight:700 !important;
        line-height:1.5 !important;
        border:1px dashed #aaa !important;
        max-width:550px !important;
        font-weight:300 !important;
    }
    .ambox td,.tmbox td{
        padding:10px !important;
    }
    .ambox-text-small,.mbox-text,.Note td{
        font-size:14px !important;
        font-weight:300 !important;
        color:#000 !important;
    }
    .ambox-image,.mbox-image{
        display:none;
    }
    #request_for_deletion,.ambox-serious{
        background:rgba(255,0,0,0.1) !important;
        border:1px dashed rgba(255,0,0,0.15) !important;
        color:#a00000 !important;
    }
    .tmbox-notice,.tmbox-move,.Note{
        background:rgba(150,75,0,0.1) !important;
        border:1px dashed rgba(150,75,0,0.15) !important;
    }
    .informationbox{
        background:#fff !important;
        padding:10px !important;
        font-size:14px !important;
    }
    .editOptions{
        background:#fff !important;
        border:0px !important;
    }
    
    /* Ссылки на категории */
    .catlinks{
        background:transparent !important;
        border:1px dashed #aaa !important;
        font-size:16px !important;
    }
    
    
    /* Таблички справа и некоторые таблички и боксы на странице */
    .infobox,.tright,.toccolours{
        border:0px !important;
        background:transparent !important;
        border-collapse:collapse !important;
    }
    .infobox td,.infobox th,.tright td,.tright th,.toccolours td,.toccolours th{
        padding:5px !important;
        background:transparent !important;
        font-weight:400 !important;
        border:1px dashed rgba(0,0,0,0.07) !important;
    }
    .image img:hover{
        opacity:0.85 !important;
    }
    
    /* Главный блок на заглавной странице */
    .globegris{
        background:transparent !important;
    }
    
    /* Мини-символы у ссылки править и примечаний 
    .mw-editsection-bracket,.references-small b,.mw-cite-backlink,.plainlinksneverexpand{
        display:none !important;
    }*/
    #floating_object{
        display:none;
    }
    
    /* Нижние ссылки по навигации и навигация по разделам навигации*/
    .navbox,.nowraplinks{
        margin:10px 0px !important;
        border-collapse:collapse !important;
    }
    .navbox td,.navbox th,.nowraplinks td,.nowraplinks th{ 
        padding:9px 6px !important;
        font-size:14px !important;
        font-weight:300 !important;
        border:1px solid #fff !important;
    }
    .NavFrame{
        border:0px !important;
        background:transparent !important;
        padding:0px !important;
    }
    .NavHead{
        margin:10px 0px !important;
        padding:12px !important;
        font-size:16px !important;
        font-weight:700 !important;
        background:transparent !important;
    }
    .Boxmerge{
        border:0px !important;
    }
    
    /* Географические координаты */
    #coordinates{
        display:none !important;
    }
    #mw-fr-revisiontag,#mw-fr-reviewnotice{
        display:none !important;
    }


sub, sup {
    line-height: 0.1em; /* prevents sub/superscripts from throwing off line spacing */
}
    
/* title */
.firstHeading {
padding: 10px 0px 38px 0px !important;
/*position: relative  !important;
z-index: -1 !important;*/
}

#column-one, #footer { display: none !important; } 

/* hide left and top panel */
#mw-panel {display:none!important;}
.noprint {display:none!important;}

#panel, #head,#page-base
  { display: none !important; } 

  
#content.mw-body {
    position: relative;
    top: 0;
    bottom: 0;
    left: 10px;
    max-width: calc(100% - 100px) !important;
    margin: 0;
    border: none !important;
    border-radius: 2px !important;
    padding: 0px 1.6em 2em 1em !important;
    background: none;
    color: #555;
}

    #content table.wikitable {
        color: #666 !important;
        border: 1px solid #EEE !important;
    }
    table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {    border: 0px solid #CCC !important;}
    
/* table of contents */
#toc {

    /*border: 1px solid #EEE !important;*/
    border: none !important;
    /*border-radius: 2px !important;*/
    font-size: 12px;
    font-weight: 400;
    background:#fff !important;
    /* make sidebar  */
    position: fixed !important;
    display: inline-block;
    top: 0;
    bottom: 0;
    left: 0;
    width: 200px;
    padding: .5em 1.5em 0 .5em;
    overflow: auto;
    /*z-index: 99999;*/
    height: 100%;
        background: #F5F5F5 center center scroll !important;
    }
    #toc > ul {
        overflow-y: auto !important;        
        height: auto;
    background: #EDEDED center center scroll;
        vertical-align: middle !important;
        display: table-cell;

        margin: auto 0 !important;
        
        
        /*display: inline-block !important;*/
    }
    
    .toc ul ul { margin-left: 1em !important; }
    .toc a {
        display: block;
        padding: 0.25em 1em;
        line-height: 1.25 !important;
    }
    /* toc headers */
    .toclevel-1 > a > .toctext {
        font-weight: 600;
        text-transform: uppercase;
        padding-top: 0.25em;
        display: inline-block;
    }
    .tocnumber {display:none;}
    .toc h2 {display:none !important;}
/*.toc:hover {
    background-color: #F5F5F5 !important; 
    box-shadow: 0 0 4px #666 !important;
    -moz-transition: 400ms;
    }*/
.toccolours, .thumb {
    border: 1px solid #EEE !important;
/*    box-shadow: 0px 0px 3px 0 #999 !important; */
    border-radius: 2px !important;
    }
div.toctitle {text-decoration: none !important;}
span.toctoggle {display:none !important;}
  

.editsection, .mw-editsection, .plainlinks.hlist.navbar {display: none !important;}

#toc a:link {color: #888 !important; text-decoration: none !important;}
#toc a:visited {color: #AAA !important;}
#toc a:hover {color: #000 !important; text-decoration: none !important; background: rgba(0,0,0,0.05);}
#toc a:active {color: #00A6FF !important; outline:none;}


/* hide hide button
.collapseButton {display:none !important;}  */
    
/* hide protected lock */
div#protected-icon {display:none !important;}

}
