css:
@-moz-document regexp("http(s|)://getpocket.com/a/(queue|favorites|archive)/.*") {
a.topic_detail {
  z-index: 1 !important;
}

a.original_url {
  overflow: visible !important;
}

a.original_url::before {
  display: block;
  content: "";
  width: 1157px;
  height: 287px;
  position: absolute;
  top: -257px;
  left: -328px;
}

li.action_mark a {
  overflow: visible !important;
}

li.action_mark a::before {
  display: block;
  content: "";
  position: absolute;
  opacity: 0;
  background: rgba(0,0,0,0.5) url(data:image/svg+xml;base64,) center no-repeat;
  background-size: 90%;
}
li.action_mark_archived a::before {
  background-image: url(data:image/svg+xml;base64,);
  background-size: 0;
}
li.action_mark a:hover::before {
  opacity: 1;
  transition: background-size 0.15s cubic-bezier(0.575, 0.000, 0.385, 1.410);
  background-size: 48px;
}

div.page_queue_list li.action_mark a::before {
  width: 100px;
  height: 80px;
  top: -17px;
  left: -829px;
}
div.page_queue_list li.action_mark_archived a::before {
  left: -832px;
}

div.page_queue_grid li.action_mark a::before {
  width: 170px;
  height: 170px;
  left: 20px;
  top: -330px;
  border-radius: 50%;
  background-position: 25% 75%;
}
div.page_queue_grid .action_mark a:hover::before {
  transition-property: all;
  transition-delay: 0.075s;
}

.pkt_toast_wrapper {
  top: 85%;
  left: 35%;
}
}
