css:
@-moz-document domain("www.wykop.pl") {
    
.pding15_0 {
    padding: 7px 0px;
}

#body-con{
    margin-top: 35px !important;
}
    

.margintop30 {
    margin-top: 10px !important;
}


.wrapper {
    max-width: 1200px;
}

nav.main {
    margin-top: 3px;
}

.pdingtop20 {
    padding-top: 7px !important;
}

#header-con {
    min-height: 5px;
}


.article .text {
    font-size: 1.2rem !important;
    margin-bottom:0.1rem;
    height:3.5em;
    overflow: hidden;
    text-overflow: ellipsis;
    text-align:left;
}

  
.link {
     margin-bottom: 1px !important;   
}

#dyingLinksBox{
    height: 1px !important; 
    visibility: hidden !important;
}

h2{
    font-size: 18px !important; 
}

.article.preview .media-content > a > img {
    width: 147px !important;
    max-height: 100px !important;
    margin-right:10px;
}

.article.preview .media-content + div {
    margin-left: 145px !important;
}
      
#w-button{
    visibility: hidden !important;   
}

.sub .wblock {
    padding-bottom: 0px !important;
}
.wblock {
    padding-bottom: 0px !important;
}

.rbl-block {
    margin-bottom: 1px !important;
}

.comments-stream > li {
    margin-bottom: 5px !important;
}

.row.elements.actions {
    text-align: right !important;
    font-size: 11px;
    margin-top: -3px;
}

.comments-stream .article {
    padding-top: 5px !important; 
    padding-bottom: 5px !important; 
}

.diggbox span:first-child
{
    background: none !important;
    border: 2px solid #4383AF;
    border-radius:5px;
}

.article.preview h2
{
    margin-bottom: 0.0rem !important;
}

.tag
{
    padding: 0 0.0rem;
}

.article.preview > div > div
{
    margin-bottom: 1px;
}

#nav .nav li a.dropdown-show.auto {
    border-style: none !important;
    padding: 0px !important;
}

#nav .nav li a.dropdown-show.auto:hover {
    border-style: none !important;
}

#nav .nav li.logged-user.ddC {
    margin-left: 0px !important;
}
    
#nav .nav li.notification.m-hide {
    margin-right: 0px !important;
}
    
#nav .nav li a.dropdown-show.ajax {
   padding-left: 1px !important;
   padding-right: 6px !important; 
}

    
#nav .nav li.notification.m-user {
    padding: 0px !important;
    margin: 0px !important;
}
   
#nav .nav li a.dropdown-show.ajax:hover {
    border-style: solid !important;
    border-bottom-width: 2px !important;
}
    
#nav .nav li a.dropdown-show.hashtag.ajax {
    padding-right: 15px !important;
    padding-left: 15px !important;
}

#nav .notification a b, .notification a b {
    right: 3px;
}
    
#nav .nav ul.clearfix.mainnav li a {

    font-size: 1.25rem !important;
    font-weight: bold !important;
    min-width: 80px !important;
    text-align: center;
    
}
    
#nav .nav ul li a {

    font-size: 1.2rem !important;
    font-weight: normal !important;
}
    
.grid-main
{
    margin-right: 320px;
}

.fix-tagline
{
    font-size:1.15rem
}

#nav .nav li a
{
    font-weight:normal;
}

.info.m-reset-float
{
    font-size:1.2rem;
    line-height: 3rem;
}

.nav > ul li a
{
    padding:5px !important;
}

.div.text > p
{
    font-size: 0.9rem !important;
}

.button.mikro *, .button.mikro
{
    font-size: 10px !important;
    border-radius:3px;
    font-weight: lighter !important;
    line-height: 15px;
    padding: 0px 0px !important;
    text-align:center;
    background-color: #fff;
    margin-top: 0px;
    height: 17px;
}

#nav .nav li a
{
    line-height: 38px  !important;
}

.article > div p.text a
{
    letter-spacing:-0.1px;
}

.w-item.mini span.title
{
    font-weight:lighter !important;
    text-align:justify !important;
}
    
i.fa
{
    width: 10px !important;
    height: 10px !important;

    margin-left: 3px !important;
    margin-right:3px !important;
}

i.fa.fa-plus
{
    width: 16px !important;
    height: 18px !important;
    margin: -1px !important;

}
    
    
.fa.fa-plus {
    margin: 0 !important;
    width: 15px !important;
    height: 18px !important;
    line-height: 17px !important;
    background-color: #e3e3e3 !important;
    border: solid;
    border-width: 1px;
    border-color: #d8d8d8;

}
    
.author i.fa-plus {
    
    color: #616161;

}
    
i.fa.fa-minus
{
    width: 16px !important;
    height: 18px !important;
    margin: -1px !important;

}
    
    
.fa.fa-minus {
    margin: 0 !important;
    width: 15px !important;
    height: 18px !important;
    line-height: 17px !important;
    background-color: #e3e3e3 !important;
    border: solid;
    border-width: 1px;
    border-color: #d8d8d8;

}
    
.author i.fa-minus {
    
    color: #616161;

}

.wrapper.nav > ul.mainnav > li > a
{
  
    
}


ul.related li > div span.data > span.cover
{
    opacity:0.6;
}

.wblock.lcontrast > div > div.text > p
{
    font-size: 1.2rem !important;    
}
    
.wblock textarea, .wblock form textarea {
    width: 100%;
    min-height: 5rem;
    font-size: 1.3rem;
}
    
.voted > span
{
    font-size:1.05rem !important;
    font-weight:lighter !important;
    padding:0 !important;
    
}

ul.related li > div > div:first-child p b.voted, .author p b.voted
{
    padding:0px 2px;
    min-width:17px !important;
    max-height:17px !important;
    line-height:17px;
}
    
.author.ellipsis span
{
   font-size:1.2rem;
   font-weight:normal;
   margin-top:-1px !important;
}
    
.author p *
{
    line-height:17px;
    
}
}

@-moz-document url-prefix("http://www.wykop.pl/wykopalisko/"), 
    url-prefix("http://www.wykop.pl/link/"),
    url-prefix("http://www.wykop.pl/hity/"),
    regexp("http://www.wykop.pl/$") {
    

#itemsStream > li.entry
{
    display: none;
}
    
}
