css:
@-moz-document domain("androidpolice.com") {
@font-face
{
font-family: "RobotoLight";
src: url(https://roboto-webfont.googlecode.com/files/Roboto-Light-webfont.woff);
}
@font-face
{
font-family: "Roboto";
src: url(http://themes.googleusercontent.com/static/fonts/roboto/v8/2UX7WLTfW3W8TclTUvlFyQ.woff);
}

/* LOGO */
img[usemap="#home_map"]{
  content:url("http://i.imgur.com/IQOT9CQ.png");
  margin-right: 20px;
}
/* Header colouring */
#header_main_bg_top{
  background-color: #3F51B5;
}
#header_main_bg_top img{
 display:none; 
}
#header_main_bg_bottom img{display:none;}
#header_main_bg_bottom{background-color: #eeeeee;}
#header{
  background-image: none;
  background-color: #3F51B5; 
}
/* Background */
#main {
background: #eeeeee no-repeat scroll 50% 0;
min-width: 1219px;
}
/* Tabs */
#header .tabbar {
list-style: none;
margin-top: 4px;
position: relative;
}
#header .tabbar .tabselected, #header .tabbar > li a:hover {
background: none;
border-radius: 0px 0px 0 0;
-moz-border-radius: 0px 0px 0 0;
-khtml-border-radius: 0px 0px 0 0;
-webkit-border-radius: 0px;
border-bottom:2px solid white;
}
/* Hide the +1 from the top as it's useless (it's in the sidebar) */
#google_plus_one{
  display:none;
}
/* Header icons */
#phone_store a{
 background-image: url("http://i.imgur.com/BnKBic9.png");
  margin-left: 20px;
}
#apparel_store a{
 background-image: url("http://i.imgur.com/9gnUv3v.png");
}
#twitter a{
 background-image: url("http://i.imgur.com/DaYwjCK.png"); 
}
#twitter a:hover{
 background-image: url("http://i.imgur.com/DaYwjCK.png"); 
}
#rss a{
 background-image: url("http://i.imgur.com/6TOW0Qx.png"); 
}
#rss a:hover{
 background-image: url("http://i.imgur.com/6TOW0Qx.png"); 
}
#tip_us a img{
  content:url("http://i.imgur.com/X8ucWFO.png");
}
/* Search box */
#search #search_box {
background-color: white;
color: black;
width: 190px;
height: 14px;
border: 0;
font-size: 10px;
padding: 2px 15px;
float: left;
border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
-webkit-border-radius: 3px;
}
#search_button{
  background-image: url(data:image/png;base64,) !important;
}
/* Main section & sidebar */
div.post, div.page, div.wp-pagenavi, div.search_hits {
padding: 25px 30px;
font-size: 14px;
margin-bottom: 15px;
background-color: #fff;
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 2px, rgba(0, 0, 0, 0.0470588) 0px 0px 0px 1px;
}
#sidebar > ul > li {
padding: 15px;
margin-bottom: 15px;
border-radius: 10px;
background-color: #fff;
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 2px, rgba(0, 0, 0, 0.0470588) 0px 0px 0px 1px;
}
#sidebar > ul > li:hover {
padding: 15px;
margin-bottom: 15px;
border-radius: 10px;
background-color: #fff;
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 2px, rgba(0, 0, 0, 0.0470588) 0px 0px 0px 1px;
}
#sidebar > ul > li {
  padding-top:0px;
}
#sidebar > ul > li:hover {
  padding-top:0px;
}
body{
  font-family:'Roboto'!important;
}
.widgettitle{
  color:black !important;

 font-family:'Roboto'!important; 
    text-shadow: 0px 0px 0px rgba(0, 0, 0, 0.15) !important;
}
  #sidebar .widgettitle {
color: white;
text-transform: uppercase;
padding: 10px;
margin: 0 -15px 10px -15px;
font-weight: normal;
background-color: #17578d;
position: relative;
font-size: 14px;
text-align: left;
 box-shadow: 0 0 0px #555; 
-moz-box-shadow: 0 0 0px #555;
 -webkit-box-shadow: 0 0 0px #555; 
}
  .chzn-container {
margin: 0px auto 0;
z-index: 100;
}
  .chzn-container-single .chzn-single {
background-color: #ffffff !important;
background-image: none !important;
 -webkit-border-radius: 3px; 
-moz-border-radius: 3px;
 border-radius: 3px; 
-moz-background-clip: padding;
-webkit-background-clip: padding-box;
background-clip: padding-box;
border: 1px solid #aaaaaa;
 -webkit-box-shadow: 0 0 0px #ffffff inset, 0 0px 0px rgba(0,0,0,0.1); 
-moz-box-shadow: 0 0 0px #ffffff inset, 0 0px 0px rgba(0,0,0,0.1);
 box-shadow: 0 0 0px #ffffff inset, 0 0px 0px rgba(0,0,0,0.1); 
display: block;
overflow: hidden;
white-space: nowrap;
position: relative;
height: 23px;
line-height: 24px;
padding: 0 0 0 8px;
color: #444444;
text-decoration: none;
}
/* Footer */
#footer {
background: #eeeeee !important;
padding: 0 0 10px 0;
}
#footer_main_bg{display:none;}
#footer_top #sidebar2 .footer_posts_excerpt > a {
color: black;
}
