}
#popular-posts ul{
  padding:10px;
}
#recent-comments{
  background-color: #fff;
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 2px, rgba(0, 0, 0, 0.0470588) 0px 0px 0px 1px;
}
#recent-comments ul{
  padding:10px;
}
#sidebar .widgettitle{
    background-color:white !important;
}
#sidebar2 .widgettitle {
font-weight: normal !important;
font-size: 14px;

font-family: "Roboto" !important;
text-align: left !important;
margin-bottom: 0px !important;
  padding-left:10px;
  padding-top:5px;
}
.footer-title{color:black;}
#footer .ap-popular-posts a{color:black;}
#footer .ap-popular-posts a:hover{color:black;}
.ap-popular-posts ul li{border-color:transparent;}
#author_bio{background-color:transparent;
-webkit-border-radius: 5px;
box-shadow: 0 0 8px transparent;
-moz-box-shadow: 0 0 8px transparent;
-webkit-box-shadow: 0 0 8px transparent;}
