#footer a {
color: black;
}
/* Shadow remove */
h1, h2, h3, h4, h5, h6 {
text-shadow: 0px 0px 0px rgba(0,0,0,0.15);
}
/* Date */
div.date {
color: black;
background: none;
height: 45px;

float: left;
font-size: 15px;
font-family: Tahoma;
left: -53px;
padding-top: 17px;
position: absolute;
text-align: center;
width: 57px;
cursor: default;
background-color: #fff;
border-radius: 2px;
box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 2px, rgba(0, 0, 0, 0.0470588) 0px 0px 0px 1px;
  z-index:-100;
}
/* Comments */
#disqus_thread{
  background-color: #fff;
border-radius: 2px;
