css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dribbble.com") {

strong.fav-number, strong.bucket-number, .vcard span.user-meta,.meta-act-views, ol.tags em, .player-info .player-stats, .profile-dash .profile-stats span.count {
    display:none!important;
  }
  .profile-stats .meta {
   color: rgba(0,0,0,0.72);
   font-weight: 500;
  }
  .dribbble-shot .fav a, .dribbble-shot .cmnt a, .dribbble-shot .views, #comments .likes-list {
    width: 0px!important;
    font-size: 0!important;
    height: 11px!important;
    line-height: 18px;
    display: inline-block!important;
    vertical-align: top!important;
  }
  .profile-tabs span.count {
    font-size: 0em;
    width: auto;
  }
  .profile-tabs span.count {
    content: 'Some'!important;
  }
  
  .profile-tabs span.count:after {
    font-size: 16px;
  }   
  h4.vcard img {
    margin-top: -0.25em!important;
    margin-right: 0.75em!important;
  }
  h4.vcard .follow-prompt {
    
    margin-top: -0.75em!important;
  }
  h1#screenshot-title {
    font-size: 0em;
  }
  h1 a {
    font-size: 24px;
    line-height: 1.4em;
  }
  #comments-section h2.section {
    font-size: 0em!important;
  }
  #comments-section h2.section:before {
    font-size: 14px;
    content: 'Add a thoughtful critique';
  }



}
