css:
@-moz-document domain("tinychat.com") {
.guest {
    background: black !important;
}

/*[[resize]]*/

/*[[maxwidth]]*/

#header{
    margin: 0 !important;
    background: black !important;
    border-radius: 0px !important;
}

#tinychat {
    background: black !important;
}

#homepage-bar {
    background: black !important;
    border: none !important;
}

#create-room-container {
    display: flex !important;
    justify-content: flex-start !important;
}

#create-room-container > .inline:nth-child(2) {
    width: none !important;
}

#homepage-text {
    display: none !important;
}

#choose-room-type {
    text-align: right !important;
}

#choose-room-type h1, #choose-room-type h2 {
    display: none !important;
}

#download-app-trigger {
    display: none !important;
}

#directory-list, #container {
    background: black !important;
}

#create-room {
    background: none !important;
}

#create-room-container > .inline:first-child {
    display: none !important;
}

.spotlight {
    background: none !important;
    box-shadow: blue 2px 2px 0px !important;
    border-radius: 20px;

}

.avatar, .room {
    background: darkslateblue !important;;
}
.room a, .room p {
    color: white !important;
}
.profile {
    background: lightskyblue !important;
}

.profile {
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
    
}

.avatar {
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
}

#footer {
    background: grey !important;
    border: none !important;
    color: lightskyblue !important;
}

div.right a {
    color: lightskyblue !important;
}
}
