css:
@-moz-document domain(ronorp.net),
               url-prefix(http://www.ronorp.net/),
               url-prefix(http://ronorp.net),
               url-prefix(http://www.ronorp.net),
               url-prefix(http://ronorp.ch),
               url-prefix(http://www.ronorp.ch)
                              {
body { 
	font-size:78.5% !important; /* for IE */
	font-family:Arial, Helvetica, sans-serif !important ;
	color:#333 !important;
	text-align:left!important;
	background-color:#FFFFFF !important;
	background:none !important;
	text-align:center !important;
	}
#app_container{
	text-align:center !important;
	}
#app_container_main{
	margin:0px auto !important;
	text-align:left !important;
	float:none !important;
	}

h2, h3, h3.title_comment, h3.text-header, h3.photo-header {
	font: bold normal normal 12px/1em Arial, Helvetica, sans-serif  !important;
	margin: 5px 0px 3px 0px !important;
	}
.text_comment, .comment, .ads-text{
	font : normal normal normal 11px/14px Arial, Helvetica, sans-serif !important;
	}
.kiosk_block {
	border:none !important;
}

/* --- ugly red removed --- */
.content_inserate .pages_arrow
{
	background-color:#9f191b !important;
}
.content_inserate .pages_links_block_inverted .pages_links_current, 
.info-kiosk-ads .kiosk_subblock h3, 
.content_inserate .pages_sort_block ul li a.selected-sort, 
.city-content-inserate h3.title_comment a,
#cat_alle-inserate
{
	color:#9f191b !important;
}

.info-kiosk-ads,
.info-kiosk-ads .kiosk_subblock
{
	border-color:#9f191b !important;
}
#banner_top,
.leader-banner,
.banner_right,
.kiosk-ads-content{
	display:none !important;
}
.form_container{
	background-color:#fff !important;
	background-image:none  !important;
}
textarea, 
textarea.dataform_message_ ,
.inputText{
	border:inset 2px gray  !important;
}


}
