@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kaskus.co.id") {
  .account-panel .user .cendol,
  .hfeed .author .cendol {
    padding: 0;
    height: 10px;
    /* background: linear-gradient(to left, #fff, #000); */
  }

  .account-panel .user .cendol .cendol,
  .hfeed .author .cendol .cendol {
    padding: 0 0 10px;
  }

  .account-panel .user .cendol img,
  .hfeed .author .cendol img {
    width: 0 !important;
    padding-right: 8px;
    border: 1px solid rgba(0, 0, 0, .1);
  }

  .account-panel .user .cendol img[src$="reputation_pos.gif"]:only-child,
  .hfeed .author .cendol img[src$="reputation_pos.gif"]:only-child {
    background: #2ea133;
  }

  .account-panel .user .cendol img[src$="reputation_pos.gif"],
  .hfeed .author .cendol img[src$="reputation_pos.gif"] {
    background: #2ea133;
  }

  .account-panel .user .cendol img[src$="reputation_highpos.gif"]:only-child,
  .hfeed .author .cendol img[src$="reputation_highpos.gif"]:only-child {
    background: #2ea133;
  }

  .account-panel .user .cendol img[src$="reputation_highpos.gif"],
  .hfeed .author .cendol img[src$="reputation_highpos.gif"] {
    background: #49ff4f;
  }

  .account-panel .user .cendol img[src$="reputation_balance.gif"],
  .hfeed .author .cendol img[src$="reputation_balance.gif"] {
    background: #6f7897;
  }

  .account-panel .user .cendol img[src$="reputation_neg.gif"]:only-child,
  .hfeed .author .cendol img[src$="reputation_neg.gif"]:only-child {
    background: #c8391b;
  }

  .account-panel .user .cendol img[src$="reputation_neg.gif"],
  .hfeed .author .cendol img[src$="reputation_neg.gif"] {
    background: #c8391b;
  }

  .account-panel .user .cendol img[src$="reputation_highneg.gif"]:only-child,
  .hfeed .author .cendol img[src$="reputation_highneg.gif"]:only-child {
    background: #ff5929;
  }

  .account-panel .user .cendol img[src$="reputation_highneg.gif"],
  .hfeed .author .cendol img[src$="reputation_highneg.gif"] {
    background: #ff5929;
  }

  .account-panel .user .cendol img[src$="reputation_off.gif"],
  .hfeed .author .cendol img[src$="reputation_off.gif"] {
    background: #000;
  }

  #timpuk label {
    padding: 50px 0 5px 0;
  }
}

/* Heavy Rotation */
