  body {
    background: #fcfe1e
                url("http://cdn.kaskus.com/images/2014/03/30/497260_20140330084400.jpg")
                50% 50% no-repeat fixed !important;
    background-size: cover;
  }

  #logo a,
  .img-a,
  #main-site-nav a,
  /*.searchlike-table,*/
  .advanced-search-sidebar {
       -moz-animation: heavy-rotation ease-in-out 48s;
         -o-animation: heavy-rotation ease-in-out 48s;
    -webkit-animation: heavy-rotation ease-in-out 48s;
            animation: heavy-rotation ease-in-out 48s;
       -moz-transform: rotate(18000deg);
         -o-transform: rotate(18000deg);
    -webkit-transform: rotate(18000deg);
            transform: rotate(18000deg);
  }

  @-moz-keyframes heavy-rotation {
    from {
      -moz-transform: rotate(0deg);
    }

    to {
      -moz-transform: rotate(18000deg);
    }
  }

  @-o-keyframes heavy-rotation {
    from {
      -o-transform: rotate(0deg);
    }

    to {
      -o-transform: rotate(18000deg);
    }
  }

  @-webkit-keyframes heavy-rotation {
    from {
      -webkit-transform: rotate(0deg);
    }

    to {
      -webkit-transform: rotate(18000deg);
    }
  }

  @keyframes heavy-rotation {
    from {
      transform: rotate(0deg);
    }

    to {
      transform: rotate(18000deg);
    }
  }
}
