@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mk.ru")
{
    
body {
    background-color: #434343;
}
	.article .content
	{
		margin-left: 10px !important;
		overflow: hidden;
		width: 100%;
	}
	.main
	{
		float: left;
		width: 100%;
	}
	.region_top_banner,
	.Banner_Top,
	.Banner_right,
	.ring,
	.Banner_1000x33,
	.banner_560_180,
	.partner1,
	.partner2,
	.partner3,
	.teaser,
	#LentaInform11078,
	 .article > hr,
	 #article-content > hr,
    .right,
   .left_block_minimized,
    .impression,
    body > iframe,
    .counters,
    .Yandex_bottom,
     div[class*="uptolike"],
     div.main div.breaking-news,
	 body > div.wrap > div  div.main > a,
	.top_banner,
	.article-footer-media,
	.article-footer > hr,
	div[id*="adfox"],
	.hidden-xs.okey-top
	{
	display: none !important;
	}

	.vk,
	.apple,
	.facebook,
	.ok,
	.twitter,
	.vr,
	.ya,
	.socialwrap,
	.gplus,
	.mobile,
	.sixteenplus
	{
		display: none !important;
	}
	.dinamic_scroll .left_block
	{
		margin-bottom: 0;
		width: 210px;
	}
	.article
	{
		float: right;
		margin-right: 10px;
		width: 77%;
	}
	.articles_top {
		width: 100%;
	}
	.links_one
	{
		margin-right: 0px;
		width: 200px !important;
	}
	.links_two
	{
		margin-right: 0px;
		width: 200px !important;
	}
	.search
	{
		float: right !important;
		margin-right: 10px;
	}
	.storie_top table td
	{
		line-height: 1em;
		padding: 5px;
		vertical-align: top;
		width: 50%;
	}
    .footer_copy p,
    .footer_copy,
    .footer_regions ul,
    .footer_menu ul,
    .footer_sites {
        font-size: 0.9em !important;
        font-family: 'Roboto Condensed',Georgia,'Century Schoolbook L',serif;
    }
	.menu
	{
		background: -moz-linear-gradient(top, #1A618F, rgba(10, 35, 59, 99));
		background: -webkit-gradient(linear, left top, left bottom, from(#1A618F), to(rgba(10, 35, 59, 99)));
		background-image:linear-gradient( to bottom, rgb(26, 97, 143), rgb(10, 35, 59) );
		}
	.menu li a
	{
		color: white;
		font-weight: bold;
		line-height: 18px;
		padding: 4px 8px;
		text-decoration: none;
	}
	.menu li
	{
		height: 22px;
		line-height: 10px;
		padding: 4px 0px;
	}
	a#menu_more_button
	{
		color: white;
		font-weight: bold;
		text-decoration: none;
	}
	a.mk_select_active
	{
		color: black;
	}
	.sub_menu li.active a
	{
		background-color: #EFF0F2;
		color: black;
	}
	.menu li a:hover
	{
		background-color: #CCF0F2;
		color: black;
	}
	.menu li.active a
	{
		background-color: #EFF0F2;
		color: black;
	}
	.menu ul
	{
		height: 30px;
		line-height: 40px;
		width: 100%;
	}
	.menu .more
	{
		color: black;
		float: right;
		padding: 1px 10px;
	}
	a.mk_select:hover
	{
		color: black !important;
	}
	.menu_more
	{
		color: black;
		height: 22px !important;
		padding: 0;
		width: 300px;
	}
.menu_more ul
	{
		background-color: #444444;
		color: black;
		height: 22px !important;
		padding: 0;
	}
	

.articles_top ul li {
    border-top: 1px solid #e7e7e7;
    padding: 7px 3%;
}

.themes ul li p {
    color: #616b6f;
    margin: 0;
}
	

	
.news ul li {
    border-top: 1px solid #e7e7e7;
    margin: 0 10px 6px;
    padding-top: 6px;
}
	
	.themes ul li {
    border-top: 1px solid #e7e7e7;
    margin-bottom: 5px;
    padding-top: 5px;
}
	
	.week_themes2 tr td .mkh2 {
    font-size: 1.3em;
}
.mkh2 {
    color: #333;
    font-family: "Roboto Condensed",Georgia,"Century Schoolbook L",serif;
    font-size: 1.4em;
    font-weight: normal;
    line-height: 1.3em;
    margin-bottom: 10px;
}
	
	#articles_top {
    width: 100%;
    margin-left: 10px;
    margin-bottom: 20px;
	}
	
}
