@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* app menu */

#appmenu_bookmarks {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: rect(0px 48px 16px 32px);
}

#BMB_bookmarkThisPage,
#appmenu_bookmarkThisPage {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: rect(0 16px 16px 0);
}

/* star button */

#star-button {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: rect(0px 16px 16px 0px);
}

#star-button:hover {
  background-image: radial-gradient(circle closest-side, hsla(45,100%,73%,.3), hsla(45,100%,73%,0));
  -moz-image-region: rect(0px 32px 16px 16px);
}

#star-button:hover:active {
  background-image: radial-gradient(circle closest-side, hsla(45,100%,73%,.1), hsla(45,100%,73%,0));
  -moz-image-region: rect(0px 48px 16px 32px);
}

/* editBookmark.png */
#star-button[starred] {
  list-style-image: url(data:image/png;base64,);
}

/* autocomplete dropdown */

.ac-result-type-bookmark,
.autocomplete-treebody::-moz-tree-image(bookmark, treecolAutoCompleteImage) {
  list-style-image: url(data:image/png;base64,);
  -moz-image-region: rect(0px 48px 16px 32px);
  width: 16px;
  height: 16px;
}

/* bookmarking panel */

#editBookmarkPanelStarIcon {
  list-style-image: url(data:image/png;base64,);
  width: 48px;
  height: 48px;
}

#editBookmarkPanelStarIcon[unstarred] {
  list-style-image: url(data:image/png;base64,);
}

