css:
@-moz-document domain("vox.com") {
    body{
       background-color: white !important;
    }
    .m-global-header__meta{
        border-bottom: skyblue 5px solid !important;
    }
    .m-global-header__user-items   {
        background-color: deepskyblue !important;
    }
    .m-global-header__tab{
        background-color: deepskyblue !important;
        border: solid 1px black !important;
    }
    .m-rock h2{
        border-bottom: solid 5px deepskyblue !important;
    }
    .m-rock--most-read__number{
       background-color: deepskyblue !important; 
    }
    .footer{
        border-top: deepskyblue 5px solid !important;
    }
    .m-breaker header h3{
        border-bottom: deepskyblue 5px solid !important;
    }
    .m-block__group-count, .m-explainer__pager__card-count, .m-explainer-embed__header{
        background-color: deepskyblue !important;
    }
   .m-global-header__dropdown--arrow {
       background-color: deepskyblue !important;
   }
   .m-hp-latest__title h4 a{
       background-color: white !important;
   }  
   .m-hp-block__container{
       border: solid 1px deepskyblue !important;
   }
   .m-hp-block__more{
       background-color: deepskyblue !important;
   }
   ::selection{background-color:black !important; color: deepskyblue !important;}
   ::moz-selection{background-color:black !important; color: deepskyblue !important;}
    
    .c-global-header .l-wrapper::after{
        background-color:deepskyblue;
    }
    
    .m-hp-block__container:hover .m-hp-block__title h2 a{
        background-color: deepskyblue;
    }
}
