css:
    /* togetter全体 */
@-moz-document domain("togetter.com") {
    /* フッタのカテゴリを上に */
    .footer_category_memu {
        border:0;
        position:absolute; top:55px; left:2%;
        width:1116px;
        height:16px;
        font-size:10px;
    }
    /* カテゴリ横の邪魔な数字 */
    .footer_category_memu li h4 span {display:none !important;}
    
    /* カテゴリ欄の調整 */
    #footer div ul li {
        text-align:center;
        margin:0px -1px;
        padding:0px 0px;
        width:73px;
        border-style:solid;
        border-width:1px;
        background-color:#5eaa9f;
        color:#2b635b !important;
    }
    .footer_category_memu li h4 a{
        text-decoration:none !important; 
        color:#ffffff !important;
        text-shadow: 1px 1px #2d534e;
    }
    .footer_category_memu > li:nth-child(14) > h4:nth-child(1) > a:nth-child(1){
        font-size:5pt
    }

/*    /* 上を受けて調整 */
/*    .contents {position:absolute; top:80px; left:5%;}
    
    /* 広告などの邪魔な物 */
    .advertise_head_box,
    #div-gpt-ad-1346816500310-0,
    #div-gpt-ad-1346816500310-1,
    .ad_custom,
    #right_wrap_bottom,
    .promotion,
    .ad_rectangle,
    .social_widget_box,
    .right_wrap > .follow_box,
    .right_wrap div[style="width:300px; height:250px;margin:10px 0;"],
    .contents_main_wrap > #social_floating_box
     {display:none !important;}
    
    /* ヘッダ */
    /* SNSボタン */
    .header_social_box {display:none !important;}

/*    /* 追随するヘッダ */
/*    #fixed_header {display:none !important;}
    
    /* フッタ */
    #footer{
        border:0;
        height:0px;
    }
    /* フッタの各小カテゴリ */
    .footer_category_list {display:none !important;}
    /* カテゴリの下のいろんなの */
    .column {display:none !important;}
    .note {display:none !important;}
    
    /* トップへ */
    #gotop {display:none !important;}
}

    /* トップページ */
@-moz-document url(http://togetter.com/) {
    /* ピックアップ */
    .topics_box > .list_title_box {display:none !important;}
    .feature_box {display:none !important;}
    /* 下部　注目のまとめ */
    .topics_column1_box {display:none !important;}
    /* まとめマイスターと公式アカウントの位置調整 */
    .topics_box > .topics_column2_box,
    .topics_box > .topics_column3_box {
        width:349px;
    }
    
    /* 全体の表示調整 */
    .thumb {display:none !important;}
    h3 {font-size:12pt !important;}
    .topics_left_box,
    .topics_right_box{
        width:349px;
    }
}

    /* 個別まとめ */
@-moz-document url-prefix(http://togetter.com/li/) {
    /* タイトルが大きすぎる */
    .info_box h1 {font-size:14pt !important;}
    /* コメント上下にある無駄な物たち */
    .topics_box,
    .contents_main_wrap > .title_simple_box,
    .contents_main_wrap > .simple_list,
    .thumb_list_box
    {display:none !important;}
    /* おすすめ商品下のSNSボタン群 */
    .social_box,
    .social_floating_box {display:none !important;}
}

    /* カテゴリ */
@-moz-document url-prefix(http://togetter.com/top/) {
    /* タイトル下のごちゃごちゃしたやつ */
    .title_category_box div p {display:none !important;}
    
    /* 全体の表示調整 */
    .topics_box>.list_title_box,
    .topics_box>.feature_box,
    .topics_box>.topics_left_box{
        width:348px;
    }
    .topics_box>.topics_right_box{
        width:348px;
        position:absolute; top:154px; left:480px;
    }
    .thumb {display:none !important;}
    h3 {font-size:12pt !important;}
    
    /* 下部の3つ */
    .topics_column1_box,
    .topics_column2_box,
    .topics_column3_box {display:none !important;}
}

    /* 個人ページ */
@-moz-document 
url-prefix("http://togetter.com/id/"),
url-prefix("http://togetter.com/fav/"),
url-prefix("http://togetter.com/com/"),
url-prefix("http://togetter.com/up/") {
    /* 左の余分な物 */
    .topics_box {display:none !important;}
    /* 右の余分な物 */
    .side_list_box {display:none !important;}
}

    /* 検索ページ */
@-moz-document
url-prefix("http://togetter.com/search") {
    /* 左の余分な物 */
    .topics_box {display:none !important;}
}
