css:
@-moz-document url-prefix("http://www.blablaland.com/") {
body {
font-family: Verdana, Geneva, sans-serif;
color: #666666;
background-color: #FBCFE9 ;
background-image : url(http://www.blablaland.com/site/images/_template/bg_smiley.png);
}
#DECO {
background-image: url(http://image.noelshack.com/fichiers/2013/25/1371947077-transparance.png);
} 

#HEADER {
background: url(http://image.noelshack.com/fichiers/2013/25/1371947077-transparance.png) no-repeat center bottom;
}

 
#HEADER_INFOS_JEU {
background-color: #FBCFE9;
color: #D42A49;
font-size: 10px;
}
a.PROFIL_DECO {
color: #D42A49;
}
a.PROFIL_DECO:hover {
color: #D42A49;
}
a.profil_pseudo {
color: #D42A49;
}
a.profil_pseudo:hover {
color: #D42A49;
}
#PROFIL_INFOS li {
color: #D42A49;
}
#PROFIL_INFOS ul a:hover li {
background-color: #BCD5DC ;
color:#BCD5DC ;
}



#MENU {
background-color: #BCD5DC;
font-size:15px;
}
#MENU li a:hover span, #MENU li:hover a span, #MENU li a:focus span, #MENU li:focus a span, #MENU li a.menu_selected span {
color: #D42A49 ;
}
#MENU li.menu_btn_jouer {
background-color: #8FA0C1 ;
color: #8FA0C1 ;
}
#MENU_BTN_CTA a {
background-color:#8FA0C1;
color:#D42A49;
}


#FOOTER_CUT {
height:20px;
background-color: #BCD5DC;
}
}
