@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("docs.python.org")
{

body {
    background-color: #111 !important;
}

div.body
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
    color: #aaa !important;
}

div.document 
{
    background: #012028 !important;
}

div.body h1, div.body h2, div.body h3, div.body h4, div.body h5, div.body h6
{
    background: none !important;
    color: #3776A3 !important;
}

div.related,
div.sphinxsidebar 
{
    background-color: #111 !important;
}

div.note {
    background-color: #111 !important;
    border-color: #000 !important;
}

div.related:first-child {
    border-color: #AAA !important;
}

div#sidebarbutton {
    background-color: #000 !important;
}

a {
    color: #4983AB !important;
}

tt
{
    background: none !important;
}

pre 
{
    background: #222 !important;
    color: #AAA !important;
}

.highlight .go /*output*/
{
    color: #AAA !important;
}

.highlight .gt, .highlight .nf /*tracebacks*/
{
    color: #4772D1 !important;
}

.highlight .nd {
    color: #aaa !important;
}

table.docutils td, table.docutils th
{
    background-color: #111 !important;
    border-color: #333 !important;
}

select, input 
{
    background-color: #111 !important;
    color: #AAA !important;
}

select
{
    border-color: #000 !important;
}

dt:target, .highlight {
    background-color: #7C6600 !important;
    color: #EEE !important;
}

div.warning,
.deprecated, .deprecated-removed {
    background-color: #211 !important;
}

div.seealso {
    background-color: #221 !important;
}

}
