css:
@-moz-document regexp("https?://(www\\.)?torrentsm((d\\.(com|eu|me))|(oldova\\.(com|org|net))).*") {
h1,h2,h3,h4,h5,h6,h1 a,h2 a,h3 a,h4 a,h5 a,h6 a {
    margin: 0;
    line-height: 1.1;
    font-weight: 400;
    font-family: lucida sans-serif,Sans,Trebuchet MS,Arial,Helvetica,sans-serif;
}

body,html {
    color: #5f5f5f;
    text-shadow: 1px 1px #FFF;
    line-height: 1.3;
    font-size: 12px;
    font-family: lucida sans-serif,Sans,Trebuchet MS,Arial,Helvetica,sans-serif;
}

div#top_menu>table>tbody>tr>td:first-child>a {
    display: block ;
    background: url("http://funkyimg.com/i/2vobS.png") no-repeat scroll 0 0 
transparent ;
    width: 280px ;
    height: 76px ;
}

#tbl_menu a {
    color: #fff ;
    padding: 0 5px ;
    height: 26px ;
    line-height: 26px ;
    display: inline-block ;
}

#tbl_menu a:hover {
    background: #4370A0 ;
    border: 1px solid #335477 ;
}

#tmd_logo {
    display: none ;
}

body, table {
    background: #fff url(http://i.imgur.com/9STaTXn.png);
}

a, #tbl_menu a {
    color: #5f5f5f !important;
    text-decoration: none;
}

a:hover {
    color: #818181;
    text-decoration: none;
}

button::-moz-focus-inner,input::-moz-focus-inner {
    border: 0;
    padding: 0;
}

input.submit:hover {
    border: 1px solid #AFAFAF;
    cursor: pointer;
}

#user_box {
    text-align: left;
    padding: 0px;
    border: 0;
}

a.button:link,a.button:visited,button,input[type='submit'],input[type='button'] {
    outline: none;
    background: url("http://i.imgur.com/bN1vUKj.png") repeat-x scroll 0 0px transparent;
    border: 1px solid #3c72a9;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    -moz-box-shadow: 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(214,214,214,.3) inset;
    -webkit-box-shadow: 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(214,214,214,.3) inset;
    box-shadow: 0 0 1px rgba(0,0,0,.2),0 1px 1px rgba(214,214,214,.3) inset;
    color: #fff;
    font-size: 11px;
    margin: 3px 5px;
    padding: 2px 10px;
    text-align: center;
    text-shadow: 0px -1px #4A4A4A;
}

button,input[type='submit'],input[type='button'] {
    padding: 2px 10px;
    height: 20px;
}

a.button:hover,button:hover,input[type='submit']:hover,input[type='button']:hover {
    background: url("http://i.imgur.com/bN1vUKj.png") repeat-x scroll 0 -70px transparent;
    cursor: pointer;
}

a.button:active,a.button.active,button:active,button.active,input[type='submit']:active,input[type='submit'].active,input[type='button']:active,input[type='button'].active {
    background: url("http://i.imgur.com/yiMd0g9.png") repeat-x scroll 0 -55px transparent;
}

.center {
    padding: 10px 0;
    text-align: center;
}

.center a {
    background: url("http://i.imgur.com/yiMd0g9.png") repeat-x scroll center -80px #F5F5F5;
    border: 1px solid #D0D0D0;
    border-radius: 3px 3px 3px 3px;
    box-shadow: 0 0 3px #D5D5D5;
    margin: 0 auto;
    padding: 2px;
}

.center a:hover {
    background: url("http://i.imgur.com/yiMd0g9.png") repeat-x scroll 0 -75px #F5F5F5;
}

.center a:focus {
    background: url("http://i.imgur.com/yiMd0g9.png") repeat-x scroll 0 -70px #F5F5F5;
}

input[type=text],input[type=password],textarea {
    background: none repeat scroll 0 0 #FBFBFB;
    border: 1px solid #D0D0D0;
    border-radius: 3px 3px 3px 3px;
    box-shadow: -1px 1px 0 #FFFFFF,0 1px 1px #D3D3D3 inset;
    padding: 4px 5px;
    color: #d0d0d0;
    -moz-transition: border linear 0.2s,box-shadow linear 0.2s;
    -webkit-transition: border linear 0.2s,box-shadow linear 0.2s;
    outline: none;
}

input[type=text]:focus,input[type=password]:focus,textarea:focus {
    background: #fff;
    color: #000;
    box-shadow: -1px 1px 0 #FFFFFF,0 0 1px #469CE1 inset;
    border: 1px solid #208CE4;
    outline: none;
}

input[type=text].inv,input[type=password].inv {
    box-shadow: -1px 1px 0 #FFFFFF,0 0 1px #E14646 inset;
    border: 1px solid #E42020;
}

input[type=text].round,input[type=password].round,textarea.round {
    border-radius: 15px;
    padding: 4px 8px;
}

textarea {
    resize: vertical;
    min-height: 50px;
    max-height: 200px;
}

#login_form {
    border: 3px solid #818181;
    -webkit-border-radius: 5px 0 5px 5px;
    -moz-border-radius: 5px 0 5px 5px;
    border-radius: 5px 0 5px 5px;
    background: #efefef;
    font-size: 11px;
    box-shadow: 0 0 3px #ABABAB inset;
    -moz-box-shadow: 0 0 3px #ABABAB inset;
    -webkit-box-shadow: 0 0 3px #ABABAB inset;
}

#login_form form {
    padding: 8px 12px;
}

#login_form input[type=submit] {
    float: right;
    box-shadow: -1px 1px 0 #FFF;
    margin: 5px 0;
}

#menutable img {
    display: none;
}
}
