css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://university.mongodb.com/") {
    .container
    {
        max-width : 100% !important;
        background: #4E362A
    }
    header.global {
    background: none repeat scroll 0 0 #4E362A;
    border-bottom: 1px solid #BEBEBE;
    box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.1);
    height: 90px;
    padding-top: 5px;
    position: relative;
    width: 100%;
    z-index: 10;
    }
    
    header.global ul.nav > li > a {
    -moz-box-orient: vertical;
    color: #FFFFFF;
    display: block;
    letter-spacing: 0;
    text-decoration: none;
    text-shadow: none;
    vertical-align: baseline;
    }
    
    header.global ul.nav > li > a:hover {
    color: #000000;
    }
    
    nav.course-material ol.course-tabs li a.active, nav.course-material .xmodule_SequenceModule nav.sequence-nav ol.course-tabs li a.seq_video.active, .xmodule_SequenceModule nav.sequence-nav nav.course-material ol.course-tabs li a.seq_video.active {
    background-color: #6BA539;
    color: #4E362A;
    }
    
    section.course-index div#accordion h3.ui-accordion-header a {
    border-radius: 0;
    box-shadow: none;
    padding-left: 19px;
    background: #4E362A
    }
    
    section.course-index div#accordion ul.ui-accordion-content {
    background: none repeat scroll 0 0 #4E362A;
    border-color: #6BA539 -moz-use-text-color;
    border-radius: 0;
    border-style: solid none;
    border-width: 0px medium;
    margin: 0;
    padding: 0;
    }
    
    
    section.course-index div#accordion ul.ui-accordion-content li a {
    background: none repeat scroll 0 0 #4E362A;
    display: block;
    padding: 5px 36px 5px 10px;
    position: relative;
    text-decoration: none;
    }
    
    .CodeMirror {
    color: #FFFFFF;
    line-height: 1;
    }
    
    section.course-index div#accordion {
    background: none repeat scroll 0 0 #4E362A;
    font-size: 18px;
    height: 700px;
    width: auto;
    }
    
    
    .cm-s-default .cm-variable {
       color: #ffffff;
    }
    .mws-scroll-wrapper {
        background: none repeat scroll 0 0 #191919;
        height: inherit;
        overflow-y: auto;
    }
    section.course-index {
    background-color: #4E362A;
    }
    div.course-wrapper section.course-content {
        background : #4E362A
    }
    .CodeMirror-wrap pre {
       background :#191919    
    }
    .cm-s-default .cm-property {
    color: #FFFFFF;
    }
    form label {
    display: block;
    color: #FFFFFF;
    }
    section.course-index div#accordion ul.ui-accordion-content li.active > a 
    {
    background: none repeat scroll 0 0 #191919;
    color: #FFFFFF;
    }
    .CodeMirror-wrap .CodeMirror-scroll {
    overflow-x: hidden;
    background: #191919;
    }
    .xmodule_CapaModule section.problem div.codeinput > .CodeMirror pre {
    background: none repeat scroll 0 0 RGBA(0,0,0,0);
    border-radius: 0;
    border-width: 0;
    font-family: inherit;
    font-size: inherit;
    margin: 0;
    padding: 0;
    resize: none;
    white-space: pre;
    color: #FFFFFF;
    border: none    
    }
    .CodeMirror-gutter {
    display: inline-block;
    height: 100%;
    margin-bottom: -32px;
    padding-bottom: 30px;
    white-space: normal;
    background: #191919;
    border: none;    
    }
    
    .xmodule_CapaModule section.problem div .grader-status {
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-top-colors: none;
    background: none repeat scroll 0 0 #191919;
    border-color: -moz-use-text-color #191919#191919;
    border-image: none;
    border-right: 1px solid #DDDDDD;
    border-style: none solid solid;
    border-width: 0 1px 1px;
    margin-bottom: 20px;
    padding: 9px;
    border: none;    
    }
    
    .xmodule_VideoSetModule div.video {
    background: none repeat scroll 0 0 #4E362A;
    border-radius: 5px;
    display: block;
    margin: 0 -12px;
    padding: 12px;
    }
    
    article, aside, details, figcaption, figure, footer, header, hgroup, nav, section {
    color: #000000;
    display: block;
}
   
}
