css:
@-moz-document regexp("https?://(www\\.)?torrentsm((d\\.(com|eu|me))|(oldova\\.(com|org|net))).*") {
html, body, table {
    background: #7D8277 url("http://i.imgur.com/iZfnu8x.jpg") fixed ;
    background-size: cover ;
}

body {
    max-width: 100% ;
    margin: auto 2%;
}

#torrents > table > tbody > tr  {
    background-color:transparent !important;
}

* {
    font-family: "Segoe UI", Tahoma, sans-serif ;
}

/* === font */

a {
    font-weight: bold ;
    color: #000 ;
    text-shadow: 1px 1px 2px #999 ;
    text-decoration: none ;
}

a:hover {
    text-shadow: 1px 0px 0px #000 ;
    text-decoration: none ;
}

a:visited, a:link:visited, #search-box a:visited, .navbg a:visited, .forumbg .header a:visited, .forabg .header a:visited, th a:visited, topictitle:visited, :root a:visited, :root a:link:visited, :root :link:visited, :root a[class]:visited, :root a[href]:visited, :root a[href]:visited {
    color: #222 ;
}

/* === search, input & buttons */

#search-terms::-moz-placeholder, textarea::-moz-placeholder {
    -moz-appearance: none ;
    color: transparent ;
    text-shadow: none ;
}

select, select:focus, select:active, input:-moz-focusring {
    outline: 0 none ;
}

select, BUTTON, button:focus, INPUT, INPUT[type="submit"], INPUT[name="search_terms"], INPUT[type="text"], INPUT[type="file"], #sel, #wordIn, #show-button:after {
    -moz-appearance: none ;
    box-shadow: inset 0px 2px 4px rgba(0,0,0, .4) ;
    background: radial-gradient(at top, rgba(255,255,255, .5), transparent)  ;
    border: 1px solid ;
    border-color: #000 #888 #888 #000 ;
    border-radius: 3px ;
    font-size: 14px ;
    color: #000 ;
    text-shadow: 1px 1px 2px #666 ;
}

input[type="reset"], input[type="button"], input[type="submit"] {
    color: #000 ;
    text-shadow: 1px 1px 2px #666 ;
}

button {
    padding: 3px ;
}

textarea {
    -moz-appearance: none ;
    background: rgba(110,110,110, .5) ;
    box-shadow: inset 0px 1px 6px 2px rgba(0,0,0, .4) ;
    border-radius: 4px ;
    border: 2px black inset ;
    color: black ;
    padding: 4px ;
}

SELECT:hover, INPUT:hover, BUTTON:hover, INPUT[type="submit"]:hover, INPUT[type="submit"]:focus {
    -moz-appearance: none ;
    background: radial-gradient(at top, rgba(255,255,255, .6), transparent) no-repeat ;
    text-shadow: 1px 0px 0px #000 ;
}

div#top_menu>table>tbody>tr>td:first-child>a {
    display: block ;
    background: url("http://funkyimg.com/i/2vobS.png") no-repeat scroll 0 0 
transparent ;
    width: 280px ;
    height: 76px ;
}

#tbl_menu a {
    color: #fff ;
    padding: 0 5px ;
    height: 26px ;
    line-height: 26px ;
    display: inline-block ;
}

#tbl_menu a:hover {
    background: #4370A0 ;
    border: 1px solid #335477 ;
}

#tmd_logo {
    display: none ;
}
}
